import React, { useState, useEffect } from "react";
import Layout from "../Components/Layout";
import { getAttendanceByStudentIndex, submitLeaveRequest, getNotificationsByStudentIndex, getUnreadNotificationCount, markNotificationAsRead, markAllNotificationsAsRead } from "../api/client";
import dayjs from "dayjs";

const ParentView = () => {
  const [studentIndex, setStudentIndex] = useState("");
  const [loading, setLoading] = useState(false);
  const [students, setStudents] = useState([]);
  const [todayRecords, setTodayRecords] = useState([]);
  const [allRecords, setAllRecords] = useState([]);
  const [error, setError] = useState("");
  const [searched, setSearched] = useState(false);

  // Notification states
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotifications, setShowNotifications] = useState(false);

  // Justification form states
  const [showJustificationForm, setShowJustificationForm] = useState(false);
  const [justificationData, setJustificationData] = useState({
    studentIndex: "",
    studentName: "",
    parentPhone: "",
    leaveDate: "",
    reason: "",
    file: null
  });
  const [submitting, setSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState("");

  const loadNotifications = async (index) => {
    try {
      const { data } = await getNotificationsByStudentIndex(index);
      setNotifications(data.notifications || []);
    } catch (err) {
      console.error("Error loading notifications:", err);
      setNotifications([]);
    }
  };

  const loadUnreadCount = async (index) => {
    try {
      const { data } = await getUnreadNotificationCount(index);
      setUnreadCount(data.count || 0);
    } catch (err) {
      console.error("Error loading unread count:", err);
      setUnreadCount(0);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!studentIndex.trim()) {
      setError("Please enter a student index number");
      return;
    }

    setLoading(true);
    setError("");
    setSearched(false);

    try {
      const { data } = await getAttendanceByStudentIndex(studentIndex);
      setStudents(data.students || []);
      setTodayRecords(data.todayRecords || []);
      setAllRecords(data.allRecords || []);
      setSearched(true);
      setError("");

      // Load notifications for this student
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error fetching attendance:", err);
      setError(err?.response?.data?.message || "Failed to fetch attendance records. Please check the student index number.");
      setStudents([]);
      setTodayRecords([]);
      setAllRecords([]);
      setSearched(false);
    } finally {
      setLoading(false);
    }
  };

  const handleMarkAsRead = async (id) => {
    try {
      await markNotificationAsRead(id);
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error marking as read:", err);
    }
  };

  const handleMarkAllAsRead = async () => {
    try {
      await markAllNotificationsAsRead(studentIndex);
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error marking all as read:", err);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "Present":
        return { bg: "#d4edda", color: "#155724" };
      case "Absent":
        return { bg: "#f8d7da", color: "#721c24" };
      case "Late":
        return { bg: "#fff3cd", color: "#856404" };
      default:
        return { bg: "#e2e3e5", color: "#6c757d" };
    }
  };

  const handleAddJustification = () => {
    setShowJustificationForm(true);
    setSubmitError("");
  };

  const handleCloseJustificationForm = () => {
    setShowJustificationForm(false);
    setJustificationData({
      studentIndex: "",
      studentName: "",
      parentPhone: "",
      leaveDate: "",
      reason: "",
      file: null
    });
    setSubmitError("");
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setSubmitError("File size should not exceed 5MB");
        return;
      }
      setJustificationData({ ...justificationData, file });
      setSubmitError("");
    }
  };

  const handleSubmitJustification = async (e) => {
    e.preventDefault();

    if (!justificationData.studentIndex.trim()) {
      setSubmitError("Please enter student index number");
      return;
    }
    if (!justificationData.studentName.trim()) {
      setSubmitError("Please enter student name");
      return;
    }
    if (!justificationData.parentPhone.trim()) {
      setSubmitError("Please enter parent phone number");
      return;
    }
    if (!justificationData.leaveDate) {
      setSubmitError("Please select the leave date");
      return;
    }
    if (!justificationData.reason.trim()) {
      setSubmitError("Please enter reason for absence/late");
      return;
    }

    setSubmitting(true);
    setSubmitError("");

    try {
      const payload = {
        studentIndex: justificationData.studentIndex,
        studentName: justificationData.studentName,
        parentPhone: justificationData.parentPhone,
        leaveDate: justificationData.leaveDate,
        reason: justificationData.reason
      };

      await submitLeaveRequest(payload);

      alert("Leave request submitted successfully! The school will review your request.");
      handleCloseJustificationForm();
    } catch (err) {
      console.error("Error submitting justification:", err);
      setSubmitError(err?.response?.data?.message || "Failed to submit leave request. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Layout>
      <div style={{ textAlign: "center", marginTop: 12, position: "relative" }}>
        <h1 style={{ margin: 0 }}>Parent View</h1>
        <p style={{ color: "var(--subtle)", marginTop: 8 }}>
          Enter your child's student index number to view attendance
        </p>

        {/* Notification Bell */}
        {searched && (
          <button
            onClick={() => setShowNotifications(true)}
            style={{
              position: "absolute",
              top: 0,
              right: 20,
              background: unreadCount > 0 ? "#ffc107" : "#6c757d",
              border: "none",
              borderRadius: "50%",
              width: 50,
              height: 50,
              cursor: "pointer",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              fontSize: 20,
              color: "white",
              boxShadow: "0 2px 8px rgba(0,0,0,0.2)",
              transition: "all 0.3s ease"
            }}
            onMouseEnter={(e) => {
              e.target.style.transform = "scale(1.1)";
            }}
            onMouseLeave={(e) => {
              e.target.style.transform = "scale(1)";
            }}
          >
            <i className="fas fa-bell"></i>
            {unreadCount > 0 && (
              <span style={{
                position: "absolute",
                top: -5,
                right: -5,
                background: "#dc3545",
                color: "white",
                borderRadius: "50%",
                width: 24,
                height: 24,
                display: "flex",
                alignItems: "center",
                justifyContent: "center",
                fontSize: 12,
                fontWeight: "bold"
              }}>
                {unreadCount}
              </span>
            )}
          </button>
        )}
      </div>

      {/* Search and Add Justification Cards */}
      <div style={{ 
        marginTop: 24, 
        display: "grid", 
        gridTemplateColumns: "1fr 1fr", 
        gap: 16,
        maxWidth: 1200,
        marginLeft: "auto",
        marginRight: "auto"
      }}>
        {/* Search Form Card */}
        <div className="panel">
          <form onSubmit={handleSearch}>
            <div className="field">
              <label>Student Index Number</label>
              <input
                type="text"
                placeholder="Enter student index number (e.g., S001, STD123)"
                value={studentIndex}
                onChange={(e) => setStudentIndex(e.target.value)}
                disabled={loading}
              />
              <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                Enter your child's index number as registered with the school
              </small>
            </div>

            {error && (
              <div style={{
                padding: 12,
                backgroundColor: "#f8d7da",
                color: "#721c24",
                borderRadius: 4,
                marginBottom: 16,
                border: "1px solid #f5c6cb"
              }}>
                <i className="fas fa-exclamation-circle" style={{ marginRight: 8 }}></i>
                {error}
              </div>
            )}

            <button 
              type="submit" 
              className="primary" 
              disabled={loading}
              style={{ width: "100%" }}
            >
              {loading ? (
                <>
                  <i className="fas fa-spinner fa-spin" style={{ marginRight: 8 }}></i>
                  Searching...
                </>
              ) : (
                <>
                  <i className="fas fa-search" style={{ marginRight: 8 }}></i>
                  View Attendance
                </>
              )}
            </button>
          </form>
        </div>

        {/* Add Justification Card */}
        <div 
          className="panel" 
          style={{ 
            padding: 20,
            cursor: "pointer",
            transition: "all 0.3s ease",
            border: "2px dashed var(--border)",
            backgroundColor: "#f8f9fa",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "280px"
          }}
          onClick={handleAddJustification}
          onMouseEnter={(e) => {
            e.currentTarget.style.borderColor = "var(--primary)";
            e.currentTarget.style.backgroundColor = "#e8f4f8";
            e.currentTarget.style.transform = "translateY(-2px)";
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.borderColor = "var(--border)";
            e.currentTarget.style.backgroundColor = "#f8f9fa";
            e.currentTarget.style.transform = "translateY(0)";
          }}
        >
          <div style={{
            width: 60,
            height: 60,
            borderRadius: "50%",
            backgroundColor: "var(--primary)",
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 28,
            marginBottom: 12
          }}>
            <i className="fas fa-plus"></i>
          </div>
          <h3 style={{ margin: 0, fontSize: 18, color: "var(--primary)", marginBottom: 8 }}>
            Add Justification
          </h3>
          <p style={{ margin: 0, color: "var(--subtle)", fontSize: 14, textAlign: "center" }}>
            Click to add a reason for absence or late arrival
          </p>
        </div>
      </div>

      {/* Results Section */}
      {searched && students.length > 0 && (
        <>
          {/* Student Info Card */}
          <div style={{ marginTop: 24 }}>
            <h2 style={{ marginBottom: 16 }}>Student Information</h2>
            {students.map((student) => (
              <div key={student._id} className="panel" style={{ padding: 20, maxWidth: 600, marginLeft: "auto", marginRight: "auto" }}>
                <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 12 }}>
                  <div style={{
                    width: 50,
                    height: 50,
                    borderRadius: "50%",
                    backgroundColor: "var(--primary)",
                    color: "white",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 20,
                    fontWeight: "bold"
                  }}>
                    {student.name.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h3 style={{ margin: 0, fontSize: 18 }}>{student.name}</h3>
                    <p style={{ margin: 0, color: "var(--subtle)", fontSize: 14 }}>
                      Index: {student.std_index}
                    </p>
                  </div>
                </div>
                <div style={{ 
                  display: "grid", 
                  gridTemplateColumns: "1fr 1fr", 
                  gap: 8,
                  marginTop: 12,
                  paddingTop: 12,
                  borderTop: "1px solid var(--border)"
                }}>
                  <div>
                    <small style={{ color: "var(--subtle)" }}>Section</small>
                    <p style={{ margin: 0, fontWeight: 500 }}>{student.section}</p>
                  </div>
                  <div>
                    <small style={{ color: "var(--subtle)" }}>Parent</small>
                    <p style={{ margin: 0, fontWeight: 500 }}>{student.parentName}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Today's Attendance */}
          <div style={{ marginTop: 32 }}>
            <h2 style={{ marginBottom: 16 }}>
              <i className="fas fa-calendar-day" style={{ marginRight: 8, color: "var(--primary)" }}></i>
              Today's Attendance ({dayjs().format("MMMM D, YYYY")})
            </h2>
            {todayRecords.length > 0 ? (
              <div className="panel">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Student Name</th>
                      <th>Index</th>
                      <th>Section</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {todayRecords.map((record) => {
                      const statusStyle = getStatusColor(record.status);
                      return (
                        <tr key={record._id}>
                          <td>{record.student?.name}</td>
                          <td>
                            <span className="badge">{record.student?.std_index}</span>
                          </td>
                          <td>{record.student?.section}</td>
                          <td>
                            <span style={{
                              padding: "4px 12px",
                              borderRadius: 4,
                              backgroundColor: statusStyle.bg,
                              color: statusStyle.color,
                              fontSize: 12,
                              fontWeight: 500
                            }}>
                              {record.status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="panel" style={{ textAlign: "center", padding: 40 }}>
                <i className="fas fa-info-circle" style={{ fontSize: 48, color: "var(--subtle)", marginBottom: 16 }}></i>
                <p style={{ color: "var(--subtle)", margin: 0 }}>
                  No attendance records for today
                </p>
              </div>
            )}
          </div>

          {/* Previous Attendance Records */}
          <div style={{ marginTop: 32 }}>
            <h2 style={{ marginBottom: 16 }}>
              <i className="fas fa-history" style={{ marginRight: 8, color: "var(--primary)" }}></i>
              Previous Attendance Records
            </h2>
            {allRecords.length > 0 ? (
              <div className="panel">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Student Name</th>
                      <th>Index</th>
                      <th>Section</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {allRecords.map((record) => {
                      const statusStyle = getStatusColor(record.status);
                      return (
                        <tr key={record._id}>
                          <td>{dayjs(record.date).format("YYYY-MM-DD")}</td>
                          <td>{record.student?.name}</td>
                          <td>
                            <span className="badge">{record.student?.std_index}</span>
                          </td>
                          <td>{record.student?.section}</td>
                          <td>
                            <span style={{
                              padding: "4px 12px",
                              borderRadius: 4,
                              backgroundColor: statusStyle.bg,
                              color: statusStyle.color,
                              fontSize: 12,
                              fontWeight: 500
                            }}>
                              {record.status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="panel" style={{ textAlign: "center", padding: 40 }}>
                <i className="fas fa-inbox" style={{ fontSize: 48, color: "var(--subtle)", marginBottom: 16 }}></i>
                <p style={{ color: "var(--subtle)", margin: 0 }}>
                  No previous attendance records found
                </p>
              </div>
            )}
          </div>
        </>
      )}

      {/* Justification Form Modal */}
      {showJustificationForm && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000,
          padding: 20
        }}>
          <div className="panel" style={{
            maxWidth: 600,
            width: "100%",
            maxHeight: "90vh",
            overflowY: "auto",
            position: "relative"
          }}>
            {/* Header */}
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: 20,
              paddingBottom: 16,
              borderBottom: "2px solid var(--border)"
            }}>
              <h2 style={{ margin: 0 }}>
                <i className="fas fa-file-medical" style={{ marginRight: 8, color: "var(--primary)" }}></i>
                Request Leave / Add Justification
              </h2>
              <button
                onClick={handleCloseJustificationForm}
                style={{
                  background: "none",
                  border: "none",
                  fontSize: 24,
                  cursor: "pointer",
                  color: "var(--subtle)",
                  padding: 0,
                  width: 32,
                  height: 32,
                  display: "flex",
                  alignItems: "center",
                  justifyContent: "center"
                }}
                title="Close"
              >
                <i className="fas fa-times"></i>
              </button>
            </div>

            {/* Form */}
            <form onSubmit={handleSubmitJustification}>
              <div className="field">
                <label>Student Index Number *</label>
                <input
                  type="text"
                  placeholder="Enter student index number (e.g., S001)"
                  value={justificationData.studentIndex}
                  onChange={(e) => setJustificationData({ ...justificationData, studentIndex: e.target.value })}
                  disabled={submitting}
                  required
                />
              </div>

              <div className="field">
                <label>Student Name *</label>
                <input
                  type="text"
                  placeholder="Enter student full name"
                  value={justificationData.studentName}
                  onChange={(e) => setJustificationData({ ...justificationData, studentName: e.target.value })}
                  disabled={submitting}
                  required
                />
              </div>

              <div className="field">
                <label>Parent Phone Number *</label>
                <input
                  type="tel"
                  placeholder="Enter parent phone number (e.g., 0706665715)"
                  value={justificationData.parentPhone}
                  onChange={(e) => setJustificationData({ ...justificationData, parentPhone: e.target.value })}
                  disabled={submitting}
                  required
                />
                <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                  Enter the contact number registered with the school
                </small>
              </div>

              <div className="field">
                <label>Leave Date *</label>
                <input
                  type="date"
                  value={justificationData.leaveDate}
                  onChange={(e) => setJustificationData({ ...justificationData, leaveDate: e.target.value })}
                  disabled={submitting}
                  required
                  style={{
                    width: "100%",
                    padding: 8,
                    borderRadius: 4,
                    border: "1px solid var(--border)",
                    fontSize: 14
                  }}
                />
                <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                  Select the date when the student was/will be absent or late
                </small>
              </div>

              <div className="field">
                <label>Reason for Absence / Late Arrival *</label>
                <textarea
                  placeholder="Please explain why the student was absent or late (e.g., Medical appointment, Family emergency, Illness, etc.)"
                  value={justificationData.reason}
                  onChange={(e) => setJustificationData({ ...justificationData, reason: e.target.value })}
                  disabled={submitting}
                  rows={5}
                  style={{
                    width: "100%",
                    padding: 8,
                    borderRadius: 4,
                    border: "1px solid var(--border)",
                    fontSize: 14,
                    fontFamily: "inherit",
                    resize: "vertical"
                  }}
                  required
                />
                <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                  Please provide detailed information about the absence or late arrival
                </small>
              </div>

              <div className="field">
                <label>Supporting Document (Optional)</label>
                <input
                  type="file"
                  accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                  onChange={handleFileChange}
                  disabled={submitting}
                  style={{
                    width: "100%",
                    padding: 8,
                    borderRadius: 4,
                    border: "1px solid var(--border)",
                    fontSize: 14
                  }}
                />
                <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                  Upload medical certificate or other supporting documents (Max 5MB)
                </small>
                {justificationData.file && (
                  <div style={{
                    marginTop: 8,
                    padding: 8,
                    backgroundColor: "#e8f4f8",
                    borderRadius: 4,
                    display: "flex",
                    alignItems: "center",
                    gap: 8
                  }}>
                    <i className="fas fa-file" style={{ color: "var(--primary)" }}></i>
                    <span style={{ fontSize: 14 }}>{justificationData.file.name}</span>
                    <button
                      type="button"
                      onClick={() => setJustificationData({ ...justificationData, file: null })}
                      style={{
                        marginLeft: "auto",
                        background: "none",
                        border: "none",
                        color: "var(--danger)",
                        cursor: "pointer",
                        fontSize: 14
                      }}
                    >
                      <i className="fas fa-times"></i>
                    </button>
                  </div>
                )}
              </div>

              {submitError && (
                <div style={{
                  padding: 12,
                  backgroundColor: "#f8d7da",
                  color: "#721c24",
                  borderRadius: 4,
                  marginBottom: 16,
                  border: "1px solid #f5c6cb"
                }}>
                  <i className="fas fa-exclamation-circle" style={{ marginRight: 8 }}></i>
                  {submitError}
                </div>
              )}

              <div style={{
                display: "flex",
                gap: 12,
                marginTop: 24
              }}>
                <button
                  type="submit"
                  className="primary"
                  disabled={submitting}
                  style={{ flex: 1 }}
                >
                  {submitting ? (
                    <>
                      <i className="fas fa-spinner fa-spin" style={{ marginRight: 8 }}></i>
                      Submitting...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-paper-plane" style={{ marginRight: 8 }}></i>
                      Submit Leave Request
                    </>
                  )}
                </button>
                <button
                  type="button"
                  className="ghost"
                  onClick={handleCloseJustificationForm}
                  disabled={submitting}
                  style={{ flex: 1 }}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ParentView;
