import React, { useState } from "react";

const initial = {
  name: "",
  std_index: "",
  section: "",
  parentName: "",
  parentPhoneNum: ""
};

const initialErrors = {};

const StudentForm = ({ onSubmit, submitting }) => {
  const [form, setForm] = useState(initial);
  const [errors, setErrors] = useState(initialErrors);

  const validateField = (key, value) => {
    switch (key) {
      case "name":
        if (value.trim() === "") return "Student name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Student name must start with a capital letter.";
        return null;
      case "std_index":
        return !/^S\d{4}$/.test(value) ? "Index number must be 'S' followed by 4 digits (e.g., S1234)." : null;
      case "section":
        return !/^\d+[A-Z]$/.test(value) ? "Section must be a number followed by an uppercase letter (e.g., 10A)." : null;
      case "parentName":
        if (value.trim() === "") return "Parent name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Parent name must start with a capital letter.";
        return null;
      case "parentPhoneNum":
        return !/^\+94\s?\d{9}$/.test(value) ? "Phone number must be +94 followed by 9 digits (e.g., +94 712345678)." : null;
      default:
        return null;
    }
  };

  const handleChange = (key) => (e) => {
    const value = e.target.value;
    const error = validateField(key, value);

    setForm((f) => ({ ...f, [key]: value }));
    setErrors((e) => ({ ...e, [key]: error }));
  };

  const submit = (e) => {
    e.preventDefault();
    // Validate all before submit
    const newErrors = {};
    let hasError = false;
    Object.keys(form).forEach(key => {
      const error = validateField(key, form[key]);
      if (error) {
        newErrors[key] = error;
        hasError = true;
      }
    });

    if (hasError) {
      setErrors(newErrors);
      return;
    }

    onSubmit(form, () => {
      setForm(initial);
      setErrors({});
    });
  };

  const getFieldStatus = (name) => {
    const value = form[name];
    const error = errors[name];
    if (value === "") return "neutral"; // Empty input is neutral
    if (error) return "invalid";
    return "valid";
  };

  const inputStyle = (status) => {
    if (status === "valid") return { borderColor: "green", boxShadow: "0 0 0 1px green" };
    if (status === "invalid") return { borderColor: "red", boxShadow: "0 0 0 1px red" };
    return {};
  };

  return (
    <form className="student-form" onSubmit={submit}>
      <div className="form-header">
        <h2>Add New Student</h2>
        <p>Please fill in all the required details</p>
      </div>

      <div className="form-grid">
        <div className="form-group">
          <label className="form-label">Student Name *</label>
          <input
            className="form-input"
            style={inputStyle(getFieldStatus("name"))}
            value={form.name}
            onChange={handleChange("name")}
            required
            placeholder="John Doe"
          />
          {getFieldStatus("name") === "invalid" && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.name}</small>}
          {getFieldStatus("name") === "valid" && <small style={{ color: "green", display: "block", marginTop: "4px" }}>✓ Valid Name</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Index Number *</label>
          <input
            className="form-input"
            style={inputStyle(getFieldStatus("std_index"))}
            value={form.std_index}
            onChange={handleChange("std_index")}
            required
            placeholder="S1234"
          />
          {getFieldStatus("std_index") === "invalid" && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.std_index}</small>}
          {getFieldStatus("std_index") === "valid" && <small style={{ color: "green", display: "block", marginTop: "4px" }}>✓ Valid Format</small>}
          {getFieldStatus("std_index") === "neutral" && <small style={{ color: "gray", display: "block", marginTop: "4px" }}>Format: S followed by 4 digits</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Class / Section *</label>
          <input
            className="form-input"
            style={inputStyle(getFieldStatus("section"))}
            value={form.section}
            onChange={handleChange("section")}
            required
            placeholder="10-A"
          />
          {getFieldStatus("section") === "invalid" && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.section}</small>}
          {getFieldStatus("section") === "valid" && <small style={{ color: "green", display: "block", marginTop: "4px" }}>✓ Valid Format</small>}
          {getFieldStatus("section") === "neutral" && <small style={{ color: "gray", display: "block", marginTop: "4px" }}>Format: Number + Uppercase Letter (e.g. 1A)</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Parent Name *</label>
          <input
            className="form-input"
            style={inputStyle(getFieldStatus("parentName"))}
            value={form.parentName}
            onChange={handleChange("parentName")}
            required
            placeholder="Jane Doe"
          />
          {getFieldStatus("parentName") === "invalid" && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.parentName}</small>}
          {getFieldStatus("parentName") === "valid" && <small style={{ color: "green", display: "block", marginTop: "4px" }}>✓ Valid Name</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Parent Phone Number *</label>
          <input
            className="form-input"
            style={inputStyle(getFieldStatus("parentPhoneNum"))}
            value={form.parentPhoneNum}
            onChange={handleChange("parentPhoneNum")}
            required
            placeholder="+94 XXX XXX XXX"
          />
          {getFieldStatus("parentPhoneNum") === "invalid" && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.parentPhoneNum}</small>}
          {getFieldStatus("parentPhoneNum") === "valid" && <small style={{ color: "green", display: "block", marginTop: "4px" }}>✓ Valid Format</small>}
          {getFieldStatus("parentPhoneNum") === "neutral" && <small style={{ color: "gray", display: "block", marginTop: "4px" }}>Format: +94 followed by 9 digits</small>}
        </div>

        <div className="form-group button-group">
          <button
            type="submit"
            className={`submit-button ${submitting ? "submitting" : ""}`}
            disabled={submitting || Object.values(errors).some(Boolean)}
          >
            {submitting ? (
              <>
                <span className="spinner"></span>
                Adding Student...
              </>
            ) : (
              "Add Student"
            )}
          </button>
        </div>
      </div>
    </form>
  );
};

export default StudentForm;
