import React from "react";
import { STATUS_OPTIONS } from "../utils/statusOptions";

const AttendanceRow = ({ student, row, onChange, disabled, acceptedLeave }) => {
  const set = (key) => (e) =>
    onChange(student._id, {
      ...row,
      [key]: e?.target ? e.target.value : e
    });

  return (
    <tr style={{ backgroundColor: acceptedLeave ? "#fff3cd" : "transparent" }}>
      <td>
        <div>
          {student.name}
          {acceptedLeave && (
            <div style={{
              fontSize: 12,
              color: "#856404",
              marginTop: 4,
              fontWeight: 500,
              display: "flex",
              alignItems: "center",
              gap: 4
            }}>
              <i className="fas fa-info-circle"></i>
              Accepted leave - Mark as Excused
            </div>
          )}
        </div>
      </td>
      <td><span className="badge">{student.std_index}</span></td>
      <td>{student.section}</td>
      <td>
        <select value={row.status} onChange={set("status")} disabled={disabled}>
          {STATUS_OPTIONS.map((opt) => (
            <option key={opt.value} value={opt.value}>{opt.label}</option>
          ))}
        </select>
      </td>
    </tr>
  );
};

export default AttendanceRow;
