import React, { useEffect, useState } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import jsPDF from "jspdf";
import "jspdf-autotable";

const Teachers = () => {
    const [teachers, setTeachers] = useState([]);
    const [searchTerm, setSearchTerm] = useState("");
    const [loading, setLoading] = useState(true);

    const fetchTeachers = async () => {
        try {
            setLoading(true);
            const response = await axios.get("http://localhost:5001/teachers");
            setTeachers(response.data.teachers || []);
        } catch (error) {
            console.error("Error fetching teachers:", error);
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchTeachers();
    }, []);

    const handleDelete = async (id) => {
        if (!window.confirm("Are you sure you want to delete this teacher?")) return;
        try {
            await axios.delete(`http://localhost:5001/teachers/${id}`);
            fetchTeachers();
        } catch (error) {
            console.error("Error deleting teacher:", error);
            alert("Error deleting teacher");
        }
    };

    const handleDownloadReport = () => {
        const doc = new jsPDF();
        doc.text("Teacher Report", 20, 10);
        doc.autoTable({
            head: [["Name", "ID", "Subject", "Email", "Phone"]],
            body: teachers.map((t) => [t.name, t.teacherId, t.subject, t.email, t.phone]),
        });
        doc.save("teachers_report.pdf");
    };

    const filteredTeachers = teachers.filter((teacher) =>
        Object.values(teacher).some((val) =>
            String(val).toLowerCase().includes(searchTerm.toLowerCase())
        )
    );

    const getInitials = (name) => {
        return name
            .split(" ")
            .map((n) => n[0])
            .join("")
            .toUpperCase()
            .substring(0, 2);
    };

    return (
        <div className="p-8 space-y-8 fade-in pb-24">
            {/* Header */}
            <div className="flex flex-col md:flex-row md:items-center justify-between gap-4">
                <div>
                    <h1 className="text-3xl font-bold text-slate-800 tracking-tight">Teachers</h1>
                    <p className="text-slate-500 mt-1">Manage teaching staff and assignments</p>
                </div>
                <div className="flex items-center gap-3">
                    <button
                        onClick={handleDownloadReport}
                        className="bg-white border border-slate-200 text-slate-600 hover:bg-slate-50 hover:text-slate-800 px-5 py-2.5 rounded-xl font-semibold transition-all shadow-sm flex items-center gap-2"
                    >
                        <i className="fa-solid fa-download"></i> Export
                    </button>
                    <Link
                        to="/teachers/add"
                        className="bg-primary-600 hover:bg-primary-700 text-white px-5 py-2.5 rounded-xl font-semibold shadow-lg shadow-primary-600/30 transition-all flex items-center gap-2"
                    >
                        <i className="fa-solid fa-plus"></i> Add Teacher
                    </Link>
                </div>
            </div>

            {/* Search */}
            <div className="relative">
                <input
                    type="text"
                    placeholder="Search teachers..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="w-full pl-12 pr-4 py-4 rounded-xl border border-slate-200 focus:border-primary-500 focus:ring-4 focus:ring-primary-500/10 outline-none transition-all text-slate-600 shadow-sm"
                />
                <i className="fa-solid fa-search absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg"></i>
            </div>

            {/* Teacher List */}
            {loading ? (
                <div className="text-center py-12">
                    <i className="fa-solid fa-circle-notch fa-spin text-4xl text-primary-500 mb-4"></i>
                    <p className="text-slate-500">Loading teachers...</p>
                </div>
            ) : filteredTeachers.length === 0 ? (
                <div className="bg-slate-50 rounded-2xl p-12 text-center border-2 border-dashed border-slate-200">
                    <div className="w-16 h-16 bg-slate-100 rounded-full flex items-center justify-center mx-auto mb-4">
                        <i className="fa-solid fa-chalkboard-user text-slate-400 text-2xl"></i>
                    </div>
                    <h3 className="text-lg font-semibold text-slate-700">No Teachers Found</h3>
                    <p className="text-slate-500 max-w-sm mx-auto mt-2 mb-6">
                        {searchTerm ? "Try adjusting your search terms." : "Get started by adding your first teacher to the system."}
                    </p>
                    <Link
                        to="/teachers/add"
                        className="inline-flex items-center gap-2 text-primary-600 font-semibold hover:text-primary-700"
                    >
                        Add New Teacher <i className="fa-solid fa-arrow-right"></i>
                    </Link>
                </div>
            ) : (
                <div className="grid grid-cols-1 md:grid-cols-2 xl:grid-cols-3 gap-6">
                    {filteredTeachers.map((teacher) => (
                        <div key={teacher._id} className="bg-white rounded-2xl p-6 border border-slate-100 shadow-sm hover:shadow-md transition-shadow group">
                            <div className="flex items-start justify-between mb-4">
                                <div className="flex items-center gap-4">
                                    <div className="w-12 h-12 rounded-full bg-primary-50 text-primary-600 flex items-center justify-center font-bold text-lg shadow-sm overflow-hidden border border-primary-100">
                                        {teacher.profilePic ? (
                                            <img
                                                src={`http://localhost:5001/${teacher.profilePic.replace(/\\\\/g, "/")}`}
                                                alt={teacher.name}
                                                className="w-full h-full object-cover"
                                            />
                                        ) : (
                                            getInitials(teacher.name)
                                        )}
                                    </div>
                                    <div>
                                        <h3 className="font-bold text-slate-800 text-lg leading-tight group-hover:text-primary-600 transition-colors">
                                            {teacher.name}
                                        </h3>
                                        <span className="text-xs font-semibold px-2 py-0.5 rounded bg-slate-100 text-slate-500 uppercase tracking-wide">
                                            {teacher.teacherId}
                                        </span>
                                    </div>
                                </div>
                                <div className="relative">
                                    <button
                                        onClick={() => handleDelete(teacher._id)}
                                        className="w-8 h-8 flex items-center justify-center rounded-lg text-slate-400 hover:text-rose-500 hover:bg-rose-50 transition-colors"
                                        title="Delete Teacher"
                                    >
                                        <i className="fa-solid fa-trash-can"></i>
                                    </button>
                                </div>
                            </div>

                            <div className="space-y-3 pt-2 border-t border-slate-50">
                                <div className="flex items-center gap-3 text-slate-600 text-sm">
                                    <div className="w-8 h-8 rounded-lg bg-orange-50 flex items-center justify-center text-orange-500 flex-shrink-0">
                                        <i className="fa-solid fa-book"></i>
                                    </div>
                                    <span className="font-medium">{teacher.subject}</span>
                                </div>
                                <div className="flex items-center gap-3 text-slate-600 text-sm">
                                    <div className="w-8 h-8 rounded-lg bg-blue-50 flex items-center justify-center text-blue-500 flex-shrink-0">
                                        <i className="fa-solid fa-envelope"></i>
                                    </div>
                                    <span className="truncate">{teacher.email}</span>
                                </div>
                                <div className="flex items-center gap-3 text-slate-600 text-sm">
                                    <div className="w-8 h-8 rounded-lg bg-emerald-50 flex items-center justify-center text-emerald-500 flex-shrink-0">
                                        <i className="fa-solid fa-phone"></i>
                                    </div>
                                    <span>{teacher.phone}</span>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Teachers;
