import React from "react";
import { Routes, Route, Navigate } from "react-router-dom";
import Login from "./pages/Login";
import Students from "./pages/Students";
import AddStudent from "./pages/AddStudent";
import Attendance from "./pages/Attendance";
import AttendanceRecords from "./pages/AttendanceRecords";
import ParentView from "./pages/ParentView";
import Teachers from "./pages/Teachers";
import AddTeacher from "./pages/AddTeacher";
import './index.css';

const App = () => {
  return (
    <div className="fade-in">
      <Routes>
        <Route path="/login" element={<Login />} />
        <Route path="/" element={<Navigate to="/login" replace />} />

        {/* Authenticated Routes wrapped in Layout for Sidebar */}
        <Route path="/students" element={<Layout><Students /></Layout>} />
        <Route path="/students/add" element={<Layout><AddStudent /></Layout>} />
        <Route path="/teachers" element={<Layout><Teachers /></Layout>} />
        <Route path="/teachers/add" element={<Layout><AddTeacher /></Layout>} />
        <Route path="/attendance" element={<Layout><Attendance /></Layout>} />
        <Route path="/records" element={<Layout><AttendanceRecords /></Layout>} />
        <Route path="/parent-view" element={<Layout><ParentView /></Layout>} />

        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
};

export default App;
