import React, { useEffect, useMemo, useState } from "react";
import Layout from "../Components/Layout";
import Select from "../Components/Select";
import AttendanceRow from "../Components/AttendanceRow";
import { getStudents, markAttendance, getAllAttendance, getPendingLeaveCount, getAllLeaveRequests, acceptLeaveRequest, rejectLeaveRequest, getAcceptedLeavesForDate, deleteAllPendingRequests } from "../api/client";
import dayjs from "dayjs";

const Attendance = () => {
  const [students, setStudents] = useState([]);
  const [section, setSection] = useState("");
  const [date, setDate] = useState(dayjs().format("YYYY-MM-DD"));
  const [rows, setRows] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [loading, setLoading] = useState(true);

  const [existingIdsForDate, setExistingIdsForDate] = useState(new Set());
  const [loadingExisting, setLoadingExisting] = useState(false);

  // Leave requests states
  const [pendingCount, setPendingCount] = useState(0);
  const [showLeaveRequests, setShowLeaveRequests] = useState(false);
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [loadingRequests, setLoadingRequests] = useState(false);
  const [acceptedLeaves, setAcceptedLeaves] = useState([]);

  const load = async () => {
    setLoading(true);
    try {
      const { data } = await getStudents();
      setStudents(data.students || []);
    } finally {
      setLoading(false);
    }
  };

  const loadPendingCount = async () => {
    try {
      const { data } = await getPendingLeaveCount();
      setPendingCount(data.count || 0);
    } catch (err) {
      console.error("Error loading pending count:", err);
    }
  };

  const loadLeaveRequests = async () => {
    setLoadingRequests(true);
    try {
      const { data } = await getAllLeaveRequests("pending");
      setLeaveRequests(data.requests || []);
      console.log("Loaded leave requests:", data.requests);
    } catch (err) {
      console.error("Error loading leave requests:", err);
      alert("Failed to load leave requests. Please check the console for details.");
      setLeaveRequests([]);
    } finally {
      setLoadingRequests(false);
    }
  };

  const handleAcceptLeave = async (id) => {
    try {
      await acceptLeaveRequest(id, "Teacher");
      alert("Leave request accepted!");
      await loadLeaveRequests();
      await loadPendingCount();
      await loadAcceptedLeaves();
    } catch (err) {
      alert(err?.response?.data?.message || "Failed to accept leave request");
    }
  };

  const handleRejectLeave = async (id) => {
    const reason = prompt("Enter rejection reason (optional):");
    try {
      await rejectLeaveRequest(id, "Teacher", reason || "No reason provided");
      alert("Leave request rejected!");
      await loadLeaveRequests();
      await loadPendingCount();
    } catch (err) {
      alert(err?.response?.data?.message || "Failed to reject leave request");
    }
  };

  const handleDeleteAllPending = async () => {
    if (!window.confirm("Are you sure you want to delete ALL pending leave requests? This action cannot be undone.")) {
      return;
    }
    try {
      const { data } = await deleteAllPendingRequests();
      alert(data.message || "All pending requests deleted successfully!");
      await loadLeaveRequests();
      await loadPendingCount();
      setShowLeaveRequests(false);
    } catch (err) {
      alert(err?.response?.data?.message || "Failed to delete pending requests");
    }
  };

  const loadAcceptedLeaves = async () => {
    if (!date) return;
    try {
      const { data } = await getAcceptedLeavesForDate(date);
      setAcceptedLeaves(data.leaveRequests || []);
    } catch (err) {
      console.error("Error loading accepted leaves:", err);
      setAcceptedLeaves([]);
    }
  };

  useEffect(() => { 
    load();
    loadPendingCount();
  }, []);

  useEffect(() => {
    loadAcceptedLeaves();
  }, [date]);

  useEffect(() => {
    const id = setInterval(() => {
      setDate(dayjs().format("YYYY-MM-DD"));
    }, 60 * 1000);
    return () => clearInterval(id);
  }, []);

  const sections = useMemo(() => {
    const set = new Set(students.map((s) => s.section));
    return Array.from(set).sort().map((s) => ({ value: s, label: s }));
  }, [students]);

  const visible = useMemo(
    () => (section ? students.filter((s) => s.section === section) : []),
    [students, section]
  );

  useEffect(() => {
    if (!section) {
      setRows({});
      return;
    }
    const map = {};
    visible.forEach((s) => {
      map[s._id] = rows[s._id] ?? { status: "Present" };
    });
    setRows(map);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [visible.length, section]);

  useEffect(() => {
    const loadExisting = async () => {
      if (!section) {
        setExistingIdsForDate(new Set());
        return;
      }
      setLoadingExisting(true);
      try {
        const { data } = await getAllAttendance();
        const list = Array.isArray(data.records) ? data.records : [];
        const ids = new Set(
          list
            .filter((r) => dayjs(r.date).format("YYYY-MM-DD") === date)
            .filter((r) => r.student?.section === section)
            .map((r) => r.student?._id)
            .filter(Boolean)
        );
        setExistingIdsForDate(ids);
      } finally {
        setLoadingExisting(false);
      }
    };
    loadExisting();
  }, [date, section]);

  const updateRow = (studentId, newRow) => {
    setRows((r) => ({ ...r, [studentId]: newRow }));
  };

  const getAcceptedLeaveForStudent = (studentIndex) => {
    return acceptedLeaves.find(leave => leave.studentIndex === studentIndex);
  };

  const nothingToSubmit = useMemo(() => {
    return visible.every((s) => existingIdsForDate.has(s._id));
  }, [visible, existingIdsForDate]);

  const submit = async () => {
    if (!section) return alert("Select a class/section first");

    setSubmitting(true);
    try {
      const toSubmit = visible.filter((s) => !existingIdsForDate.has(s._id));
      if (toSubmit.length === 0) {
        alert("Attendance already marked for all students in this section for this date.");
        return;
      }

      const payloads = toSubmit.map((s) => {
        const status = rows[s._id]?.status || "Present";
        return {
          studentId: s._id,
          date,
          status,
          notifiedParent: false // Always false when marking attendance
        };
      });

      for (const p of payloads) {
        await markAttendance(p);
      }

      setExistingIdsForDate((prev) => {
        const next = new Set(prev);
        toSubmit.forEach((s) => next.add(s._id));
        return next;
      });

      alert("Attendance marked successfully");
    } catch (e) {
      alert(e?.response?.data?.message || "Failed to mark attendance");
    } finally {
      setSubmitting(false);
    }
  };

  if (!section) {
    return (
      <Layout>
        <div style={{ textAlign: "center", marginTop: 12 }}>
          <h1 style={{ margin: "0 0 20px" }}>Mark Attendance</h1>
          <div style={{ width: "100%", display: "flex", justifyContent: "center" }}>
            <div style={{ display: "flex", gap: 16 }}>
              <div style={{ width: 320 }}>
                <Select
                  label=""
                  value={section}
                  onChange={setSection}
                  options={[{ value: "", label: "Select Section" }, ...sections]}
                />
              </div>
              <div style={{ width: 320 }}>
                <input
                  type="date"
                  value={date}
                  readOnly
                  style={{
                    width: "100%",
                    padding: "10px 12px",
                    background: "var(--muted)",
                    color: "var(--text)",
                    border: "1px solid var(--border)",
                    borderRadius: 8,
                    textAlign: "center",
                    pointerEvents: "none"
                  }}
                  aria-label="Date"
                />
              </div>
            </div>
          </div>
        </div>

        {/* Pending Leave Requests Notification */}
        {pendingCount > 0 && (
          <div style={{
            marginTop: 16,
            padding: 16,
            backgroundColor: "#fff3cd",
            border: "1px solid #ffc107",
            borderRadius: 8,
            display: "flex",
            alignItems: "center",
            justifyContent: "space-between",
            maxWidth: 800,
            margin: "16px auto 0"
          }}>
            <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
              <i className="fas fa-exclamation-triangle" style={{ fontSize: 24, color: "#856404" }}></i>
              <div>
                <strong style={{ color: "#856404" }}>
                  {pendingCount} Leave Request{pendingCount > 1 ? 's' : ''} Pending
                </strong>
                <p style={{ margin: "4px 0 0", fontSize: 14, color: "#856404" }}>
                  Recent justification requests are waiting for your review
                </p>
              </div>
            </div>
            <button
              onClick={() => {
                setShowLeaveRequests(true);
                loadLeaveRequests();
              }}
              style={{
                padding: "8px 16px",
                backgroundColor: "#ffc107",
                color: "#000",
                border: "none",
                borderRadius: 4,
                cursor: "pointer",
                fontWeight: 500,
                whiteSpace: "nowrap"
              }}
            >
              Check Now
            </button>
          </div>
        )}

        <div className="panel" style={{ padding: 24, textAlign: "center", color: "#aaa", marginTop: 16 }}>
          Select a section to begin marking attendance
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="header" style={{ flexDirection: "column", alignItems: "center" }}>
        <h1 style={{ margin: "0 0 14px" }}>Mark Attendance</h1>
        <div style={{ width: "100%", display: "flex", justifyContent: "center" }}>
          <div style={{ display: "flex", gap: 16 }}>
            <div style={{ width: 320 }}>
              <Select
                label=""
                value={section}
                onChange={setSection}
                options={[{ value: "", label: "Select Section" }, ...sections]}
              />
            </div>
            <div style={{ width: 320 }}>
              <input
                type="date"
                value={date}
                readOnly
                style={{
                  width: "100%",
                  padding: "10px 12px",
                  background: "var(--muted)",
                  color: "var(--text)",
                  border: "1px solid var(--border)",
                  borderRadius: 8,
                  textAlign: "center",
                  pointerEvents: "none"
                }}
                aria-label="Date"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Pending Leave Requests Notification */}
      {pendingCount > 0 && (
        <div style={{
          marginTop: 16,
          padding: 16,
          backgroundColor: "#fff3cd",
          border: "1px solid #ffc107",
          borderRadius: 8,
          display: "flex",
          alignItems: "center",
          justifyContent: "space-between"
        }}>
          <div style={{ display: "flex", alignItems: "center", gap: 12 }}>
            <i className="fas fa-exclamation-triangle" style={{ fontSize: 24, color: "#856404" }}></i>
            <div>
              <strong style={{ color: "#856404" }}>
                {pendingCount} Leave Request{pendingCount > 1 ? 's' : ''} Pending
              </strong>
              <p style={{ margin: "4px 0 0", fontSize: 14, color: "#856404" }}>
                Recent justification requests are waiting for your review
              </p>
            </div>
          </div>
          <button
            type="button"
            onClick={() => {
              setShowLeaveRequests(true);
              loadLeaveRequests();
            }}
            style={{
              padding: "8px 16px",
              backgroundColor: "#ffc107",
              color: "#000",
              border: "none",
              borderRadius: 4,
              cursor: "pointer",
              fontWeight: 500,
              whiteSpace: "nowrap"
            }}
          >
            Check Now
          </button>
        </div>
      )}

      <div className="panel">
        {loadingExisting && <p style={{ textAlign: "center", color: "#aaa" }}>Loading existing records...</p>}
        {!loadingExisting && (
          <table className="table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Index</th>
                <th>Section</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {visible.map((s) => (
                <AttendanceRow
                  key={s._id}
                  student={s}
                  row={rows[s._id] || { status: "Present" }}
                  onChange={(newRow) => updateRow(s._id, newRow)}
                  alreadyMarked={existingIdsForDate.has(s._id)}
                  acceptedLeave={getAcceptedLeaveForStudent(s.std_index)}
                />
              ))}
            </tbody>
          </table>
        )}
      </div>

      <div style={{ display: "flex", gap: 8, justifyContent: "flex-end", marginTop: 16 }}>
        <button className="ghost" onClick={() => setRows({})}>Reset</button>
        <button
          onClick={submit}
          disabled={submitting || !section || nothingToSubmit}
        >
          {submitting ? "Submitting..." : "Submit Attendance"}
        </button>
      </div>

      {/* Leave Requests Review Modal */}
      {showLeaveRequests && (
        <div style={{
          position: "fixed",
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backgroundColor: "rgba(0, 0, 0, 0.5)",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          zIndex: 1000,
          padding: 20
        }}>
          <div className="panel" style={{
            maxWidth: 900,
            width: "100%",
            maxHeight: "90vh",
            overflowY: "auto"
          }}>
            <div style={{
              display: "flex",
              justifyContent: "space-between",
              alignItems: "center",
              marginBottom: 20,
              paddingBottom: 16,
              borderBottom: "2px solid var(--border)"
            }}>
              <h2 style={{ margin: 0 }}>
                <i className="fas fa-clipboard-list" style={{ marginRight: 8, color: "var(--primary)" }}></i>
                Pending Leave Requests
              </h2>
              <div style={{ display: "flex", gap: 12, alignItems: "center" }}>
                {leaveRequests.length > 0 && (
                  <button
                    onClick={handleDeleteAllPending}
                    className="danger"
                    style={{
                      padding: "6px 12px",
                      fontSize: 14,
                      display: "flex",
                      alignItems: "center",
                      gap: 6
                    }}
                  >
                    <i className="fas fa-trash-alt"></i>
                    Delete All Pending
                  </button>
                )}
                <button
                  onClick={() => setShowLeaveRequests(false)}
                  style={{
                    background: "none",
                    border: "none",
                    fontSize: 24,
                    cursor: "pointer",
                    color: "var(--subtle)"
                  }}
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
            </div>

            {loadingRequests ? (
              <p style={{ textAlign: "center", padding: 40 }}>Loading requests...</p>
            ) : leaveRequests.length === 0 ? (
              <p style={{ textAlign: "center", padding: 40, color: "var(--subtle)" }}>
                No pending leave requests
              </p>
            ) : (
              <div style={{ display: "flex", flexDirection: "column", gap: 16 }}>
                {leaveRequests.map((request) => (
                  <div key={request._id} className="panel" style={{ padding: 16, backgroundColor: "#f8f9fa" }}>
                    <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12, marginBottom: 12 }}>
                      <div>
                        <small style={{ color: "var(--subtle)" }}>Student Name</small>
                        <p style={{ margin: 0, fontWeight: 500 }}>{request.studentName}</p>
                      </div>
                      <div>
                        <small style={{ color: "var(--subtle)" }}>Index Number</small>
                        <p style={{ margin: 0, fontWeight: 500 }}>{request.studentIndex}</p>
                      </div>
                      <div>
                        <small style={{ color: "var(--subtle)" }}>Parent Phone</small>
                        <p style={{ margin: 0, fontWeight: 500 }}>{request.parentPhone}</p>
                      </div>
                      <div>
                        <small style={{ color: "var(--subtle)" }}>Leave Date</small>
                        <p style={{ margin: 0, fontWeight: 500 }}>{dayjs(request.leaveDate).format("YYYY-MM-DD")}</p>
                      </div>
                    </div>
                    <div style={{ marginBottom: 12 }}>
                      <small style={{ color: "var(--subtle)" }}>Reason</small>
                      <p style={{ margin: "4px 0 0", padding: 12, backgroundColor: "white", borderRadius: 4 }}>
                        {request.reason}
                      </p>
                    </div>
                    <div style={{ marginBottom: 12 }}>
                      <small style={{ color: "var(--subtle)" }}>Supporting Document</small>
                      {request.documentPath ? (
                        <div style={{
                          marginTop: 4,
                          padding: 12,
                          backgroundColor: "white",
                          borderRadius: 4,
                          display: "flex",
                          alignItems: "center",
                          gap: 8
                        }}>
                          <i className="fas fa-file-alt" style={{ color: "var(--primary)", fontSize: 20 }}></i>
                          <span style={{ flex: 1, fontSize: 14 }}>
                            {request.documentPath.split('/').pop() || 'Uploaded Document'}
                          </span>
                          <a
                            href={`http://localhost:5000/${request.documentPath}`}
                            target="_blank"
                            rel="noopener noreferrer"
                            style={{
                              padding: "4px 12px",
                              backgroundColor: "var(--primary)",
                              color: "white",
                              borderRadius: 4,
                              textDecoration: "none",
                              fontSize: 12,
                              fontWeight: 500
                            }}
                          >
                            <i className="fas fa-download" style={{ marginRight: 4 }}></i>
                            View/Download
                          </a>
                        </div>
                      ) : (
                        <p style={{ margin: "4px 0 0", padding: 12, backgroundColor: "white", borderRadius: 4, color: "var(--subtle)", fontSize: 14 }}>
                          <i className="fas fa-info-circle" style={{ marginRight: 6 }}></i>
                          No document uploaded
                        </p>
                      )}
                    </div>
                    <div style={{ display: "flex", gap: 8, justifyContent: "flex-end" }}>
                      <button
                        onClick={() => handleRejectLeave(request._id)}
                        className="danger"
                        style={{ padding: "8px 16px" }}
                      >
                        <i className="fas fa-times" style={{ marginRight: 6 }}></i>
                        Reject
                      </button>
                      <button
                        onClick={() => handleAcceptLeave(request._id)}
                        className="success"
                        style={{ padding: "8px 16px" }}
                      >
                        <i className="fas fa-check" style={{ marginRight: 6 }}></i>
                        Accept
                      </button>
                    </div>
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      )}
    </Layout>
  );
};

export default Attendance;
