import React from "react";

const TopBar = () => {
    return (
        <div className="h-20 bg-white/80 backdrop-blur-md border-b border-slate-200 sticky top-0 z-40 w-full flex items-center justify-center px-8">
            {/* Centered Title with Logo */}
            <div className="absolute left-1/2 top-1/2 -translate-x-1/2 -translate-y-1/2 text-center flex items-center gap-3">
                <div className="w-10 h-10 bg-primary-600 rounded-lg flex items-center justify-center text-white shadow-lg shadow-primary-500/30">
                    <i className="fa-solid fa-building-columns text-lg"></i>
                </div>
                <h1 className="text-2xl font-extrabold text-slate-900 tracking-tight font-display">
                    CMB INTERNATIONAL COLLEGE
                </h1>
            </div>

            {/* Right Actions (Placeholder) */}
            <div className="ml-auto flex items-center gap-4">
                <button className="w-10 h-10 rounded-full bg-slate-50 hover:bg-slate-100 flex items-center justify-center text-slate-500 transition-colors">
                    <i className="fa-regular fa-bell"></i>
                </button>
            </div>
        </div>
    );
};

export default TopBar;
