import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import Layout from "../Components/Layout";
import { getAttendanceByStudentIndex, submitLeaveRequest, getNotificationsByStudentIndex, getUnreadNotificationCount, markNotificationAsRead, markAllNotificationsAsRead } from "../api/client";
import dayjs from "dayjs";

const ParentView = () => {
  const navigate = useNavigate();
  const [studentIndex, setStudentIndex] = useState("");

  const userRole = localStorage.getItem("userRole");
  const parentIndex = localStorage.getItem("parentStudentIndex");

  useEffect(() => {
    if (userRole === "Teacher" || userRole === "Admin") {
      navigate("/students");
    }

    // Auto-load data for parents
    if (userRole === "Parent" && parentIndex) {
      setStudentIndex(parentIndex);
      // Trigger data loading
      const fetchData = async () => {
        setLoading(true);
        setError("");
        try {
          const { data } = await getAttendanceByStudentIndex(parentIndex);
          setStudents(data.students || []);
          setTodayRecords(data.todayRecords || []);
          setAllRecords(data.allRecords || []);
          setSearched(true);
          await loadNotifications(parentIndex);
          await loadUnreadCount(parentIndex);
        } catch (err) {
          setError("Failed to fetch child's records automatically.");
        } finally {
          setLoading(false);
        }
      };
      fetchData();
    }
  }, [userRole, parentIndex, navigate]);

  const [loading, setLoading] = useState(false);
  const [students, setStudents] = useState([]);
  const [todayRecords, setTodayRecords] = useState([]);
  const [allRecords, setAllRecords] = useState([]);
  const [error, setError] = useState("");
  const [searched, setSearched] = useState(false);

  // Notification states
  const [notifications, setNotifications] = useState([]);
  const [unreadCount, setUnreadCount] = useState(0);
  const [showNotifications, setShowNotifications] = useState(false);

  // Justification form states
  const [showJustificationForm, setShowJustificationForm] = useState(false);
  const [justificationData, setJustificationData] = useState({
    studentIndex: "",
    studentName: "",
    parentPhone: "",
    leaveDate: "",
    reason: "",
    file: null
  });
  const [submitting, setSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState("");

  const loadNotifications = async (index) => {
    try {
      const { data } = await getNotificationsByStudentIndex(index);
      setNotifications(data.notifications || []);
    } catch (err) {
      console.error("Error loading notifications:", err);
      setNotifications([]);
    }
  };

  const loadUnreadCount = async (index) => {
    try {
      const { data } = await getUnreadNotificationCount(index);
      setUnreadCount(data.count || 0);
    } catch (err) {
      console.error("Error loading unread count:", err);
      setUnreadCount(0);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!studentIndex.trim()) {
      setError("Please enter a student index number");
      return;
    }

    setLoading(true);
    setError("");
    setSearched(false);

    try {
      const { data } = await getAttendanceByStudentIndex(studentIndex);
      setStudents(data.students || []);
      setTodayRecords(data.todayRecords || []);
      setAllRecords(data.allRecords || []);
      setSearched(true);
      setError("");

      // Load notifications for this student
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error fetching attendance:", err);
      setError(err?.response?.data?.message || "Failed to fetch attendance records. Please check the student index number.");
      setStudents([]);
      setTodayRecords([]);
      setAllRecords([]);
      setSearched(false);
    } finally {
      setLoading(false);
    }
  };

  const handleMarkAsRead = async (id) => {
    try {
      await markNotificationAsRead(id);
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error marking as read:", err);
    }
  };

  const handleMarkAllAsRead = async () => {
    try {
      await markAllNotificationsAsRead(studentIndex);
      await loadNotifications(studentIndex);
      await loadUnreadCount(studentIndex);
    } catch (err) {
      console.error("Error marking all as read:", err);
    }
  };

  const handleAddJustification = () => {
    setShowJustificationForm(true);
    setSubmitError("");
    // Pre-fill if student data is available
    if (students.length > 0) {
      setJustificationData(prev => ({
        ...prev,
        studentIndex: students[0].std_index,
        studentName: students[0].name,
        parentPhone: students[0].parentPhoneNum
      }));
    }
  };

  const handleCloseJustificationForm = () => {
    setShowJustificationForm(false);
    setJustificationData({
      studentIndex: "",
      studentName: "",
      parentPhone: "",
      leaveDate: "",
      reason: "",
      file: null
    });
    setSubmitError("");
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setSubmitError("File size should not exceed 5MB");
        return;
      }
      setJustificationData({ ...justificationData, file });
      setSubmitError("");
    }
  };

  const handleSubmitJustification = async (e) => {
    e.preventDefault();

    if (!justificationData.studentIndex.trim()) {
      setSubmitError("Please enter student index number");
      return;
    }
    if (!justificationData.studentName.trim()) {
      setSubmitError("Please enter student name");
      return;
    }
    if (!justificationData.parentPhone.trim()) {
      setSubmitError("Please enter parent phone number");
      return;
    }
    if (!justificationData.leaveDate) {
      setSubmitError("Please select the leave date");
      return;
    }
    if (!justificationData.reason.trim()) {
      setSubmitError("Please enter reason for absence/late");
      return;
    }

    setSubmitting(true);
    setSubmitError("");

    try {
      const payload = {
        studentIndex: justificationData.studentIndex,
        studentName: justificationData.studentName,
        parentPhone: justificationData.parentPhone,
        leaveDate: justificationData.leaveDate,
        reason: justificationData.reason
      };

      await submitLeaveRequest(payload);

      alert("Leave request submitted successfully! The school will review your request.");
      handleCloseJustificationForm();
    } catch (err) {
      console.error("Error submitting justification:", err);
      setSubmitError(err?.response?.data?.message || "Failed to submit leave request. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Layout>
      <div className="flex flex-col items-center justify-center mb-12 text-center relative">
        <h1 className="text-4xl font-extrabold text-slate-900 tracking-tight mb-3">Parent Portal</h1>
        <p className="text-lg text-slate-500 max-w-lg">
          Monitor your child's attendance and stay updated with real-time notifications.
        </p>

        {/* Notification Bell */}
        {searched && (
          <div className="absolute top-0 right-0 md:right-4">
            <button
              onClick={() => setShowNotifications(true)}
              className={`
                relative w-12 h-12 rounded-full flex items-center justify-center shadow-lg transition-all hover:scale-110 active:scale-95
                ${unreadCount > 0
                  ? "bg-amber-400 text-white shadow-amber-400/40 animate-pulse-slow"
                  : "bg-white text-slate-400 shadow-slate-200 border border-slate-100 hover:text-primary-600"
                }
              `}
              title="View Notifications"
            >
              <i className="fas fa-bell text-xl"></i>
              {unreadCount > 0 && (
                <span className="absolute -top-1 -right-1 flex h-5 w-5 items-center justify-center rounded-full bg-rose-500 text-[10px] font-bold text-white shadow-sm ring-2 ring-white">
                  {unreadCount}
                </span>
              )}
            </button>
          </div>
        )}
      </div>

      <div className="max-w-4xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-6 mb-12">
        {/* Search Card - Only show to non-parents (e.g. Admin) */}
        {userRole !== "Parent" && (
          <div className="glass-panel p-8 rounded-3xl border border-white/50 shadow-xl shadow-slate-200/50 relative overflow-hidden group">
            <div className="absolute top-0 right-0 w-32 h-32 bg-primary-100 rounded-bl-full -mr-10 -mt-10 opacity-50 group-hover:scale-110 transition-transform duration-500"></div>

            <div className="relative z-10">
              <h3 className="text-xl font-bold text-slate-800 mb-4 flex items-center gap-2">
                <i className="fas fa-search text-primary-500"></i> Find Student
              </h3>


              <form onSubmit={handleSearch} className="flex flex-col gap-4">
                <div>
                  <label className="block text-sm font-medium text-slate-600 mb-1.5 ml-1">Student Index Number</label>
                  <div className="relative">
                    <input
                      type="text"
                      className="input-field pl-11 w-full text-lg py-3"
                      placeholder="e.g., STD123"
                      value={studentIndex}
                      onChange={(e) => setStudentIndex(e.target.value)}
                      disabled={loading}
                    />
                    <i className="fas fa-id-card absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg"></i>
                  </div>
                  <p className="text-xs text-slate-400 mt-2 ml-1">
                    Enter the unique index number provided by the school
                  </p>
                </div>

                {error && (
                  <div className="bg-rose-50 border border-rose-100 text-rose-600 text-sm p-4 rounded-xl flex items-start gap-3">
                    <i className="fas fa-circle-exclamation mt-0.5"></i>
                    <span>{error}</span>
                  </div>
                )}

                <button
                  type="submit"
                  className="btn-primary py-3.5 text-base shadow-lg shadow-primary-500/20 mt-2"
                  disabled={loading}
                >
                  {loading ? (
                    <span className="flex items-center justify-center gap-2">
                      <i className="fas fa-circle-notch fa-spin"></i> Searching...
                    </span>
                  ) : (
                    <span className="flex items-center justify-center gap-2">
                      View Attendance <i className="fas fa-arrow-right text-sm opacity-80"></i>
                    </span>
                  )}
                </button>
              </form>
            </div>
          </div>
        )}

        {/* Action Card */}
        <button
          onClick={handleAddJustification}
          className="glass-panel p-8 rounded-3xl border border-dashed border-slate-300 hover:border-primary-400 shadow-sm hover:shadow-xl hover:shadow-primary-500/10 transition-all duration-300 flex flex-col items-center justify-center text-center group bg-slate-50/50 hover:bg-white"
        >
          <div className="w-16 h-16 rounded-2xl bg-white shadow-sm border border-slate-100 flex items-center justify-center text-primary-500 text-2xl mb-4 group-hover:scale-110 group-hover:bg-primary-500 group-hover:text-white transition-all duration-300">
            <i className="fas fa-file-medical"></i>
          </div>
          <h3 className="text-xl font-bold text-slate-800 mb-2 group-hover:text-primary-700 transition-colors">Submit Leave Request</h3>
          <p className="text-slate-500 text-sm max-w-xs group-hover:text-slate-600">
            Click here to provide a justification for an absence or late arrival, or to request upcoming leave.
          </p>
        </button>
      </div>

      {/* Results Section */}
      {searched && students.length > 0 && (
        <div className="animate-fade-in space-y-8">

          {/* Student Info */}
          {students.map((student) => (
            <div key={student._id} className="card p-8 border-l-4 border-l-primary-500">
              <div className="flex flex-col md:flex-row items-center gap-6">
                <div className="w-20 h-20 rounded-full bg-gradient-brand text-white flex items-center justify-center text-3xl font-bold shadow-lg shadow-primary-500/20">
                  {student.name.charAt(0).toUpperCase()}
                </div>
                <div className="text-center md:text-left flex-1">
                  <h2 className="text-2xl font-bold text-slate-900 mb-1">{student.name}</h2>
                  <div className="flex flex-wrap justify-center md:justify-start gap-3 mt-2">
                    <span className="badge badge-slate text-sm px-3 py-1">
                      <i className="fas fa-fingerprint mr-1.5 opacity-60"></i> {student.std_index}
                    </span>
                    <span className="badge badge-primary text-sm px-3 py-1">
                      <i className="fas fa-layer-group mr-1.5 opacity-60"></i> {student.section}
                    </span>
                  </div>
                </div>
                <div className="bg-slate-50 p-4 rounded-xl border border-slate-100 min-w-[200px]">
                  <p className="text-xs text-slate-500 uppercase tracking-wider font-semibold mb-1">Parent / Guardian</p>
                  <p className="font-bold text-slate-800 text-lg">{student.parentName}</p>
                </div>
              </div>
            </div>
          ))}

          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            {/* Today's Status */}
            <div className="lg:col-span-1">
              <h3 className="text-lg font-bold text-slate-800 mb-4 flex items-center gap-2">
                <i className="fas fa-calendar-day text-primary-500"></i> Today's Status
              </h3>
              {todayRecords.length > 0 ? (
                <div className="card p-0 overflow-hidden">
                  {todayRecords.map((record) => (
                    <div key={record._id} className="p-6 text-center">
                      <div className="text-sm text-slate-500 mb-2 font-medium">{dayjs().format("MMMM D, YYYY")}</div>
                      <div className={`
                            inline-flex flex-col items-center justify-center w-full py-6 rounded-2xl mb-2
                            ${record.status === 'Present' ? 'bg-emerald-50 text-emerald-700' : ''}
                            ${record.status === 'Absent' ? 'bg-rose-50 text-rose-700' : ''}
                            ${record.status === 'Late' ? 'bg-amber-50 text-amber-700' : ''}
                            ${record.status === 'Excused' ? 'bg-blue-50 text-blue-700' : ''}
                         `}>
                        <div className={`
                               w-16 h-16 rounded-full flex items-center justify-center text-3xl mb-3 shadow-sm
                               ${record.status === 'Present' ? 'bg-white text-emerald-500' : ''}
                               ${record.status === 'Absent' ? 'bg-white text-rose-500' : ''}
                               ${record.status === 'Late' ? 'bg-white text-amber-500' : ''}
                               ${record.status === 'Excused' ? 'bg-white text-blue-500' : ''}
                            `}>
                          {record.status === 'Present' && <i className="fas fa-check"></i>}
                          {record.status === 'Absent' && <i className="fas fa-times"></i>}
                          {record.status === 'Late' && <i className="fas fa-clock"></i>}
                          {record.status === 'Excused' && <i className="fas fa-file-medical"></i>}
                        </div>
                        <span className="text-2xl font-bold">{record.status}</span>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="card p-8 flex flex-col items-center justify-center text-center text-slate-400 min-h-[200px]">
                  <i className="fas fa-calendar-minus text-4xl mb-3 opacity-50"></i>
                  <p>No record for today yet.</p>
                </div>
              )}
            </div>

            {/* History */}
            <div className="lg:col-span-2">
              <h3 className="text-lg font-bold text-slate-800 mb-4 flex items-center gap-2">
                <i className="fas fa-history text-primary-500"></i> Recent History
              </h3>
              <div className="card overflow-hidden">
                <div className="overflow-x-auto max-h-[400px] overflow-y-auto custom-scrollbar">
                  <table className="w-full text-left border-collapse">
                    <thead className="bg-slate-50 sticky top-0 z-10 shadow-sm">
                      <tr className="text-xs uppercase tracking-wider text-slate-500 font-semibold">
                        <th className="px-6 py-4 border-b border-slate-200">Date</th>
                        <th className="px-6 py-4 border-b border-slate-200">Day</th>
                        <th className="px-6 py-4 border-b border-slate-200 text-right">Status</th>
                      </tr>
                    </thead>
                    <tbody className="divide-y divide-slate-100">
                      {allRecords.length > 0 ? (
                        allRecords.map((record) => (
                          <tr key={record._id} className="hover:bg-slate-50/50">
                            <td className="px-6 py-4 font-mono text-sm text-slate-600 font-medium">
                              {dayjs(record.date).format("MMM DD, YYYY")}
                            </td>
                            <td className="px-6 py-4 text-sm text-slate-500">
                              {dayjs(record.date).format("dddd")}
                            </td>
                            <td className="px-6 py-4 text-right">
                              <span className={`
                                  inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-bold
                                  ${record.status === 'Present' ? 'bg-emerald-100 text-emerald-800' : ''}
                                  ${record.status === 'Absent' ? 'bg-rose-100 text-rose-800' : ''}
                                  ${record.status === 'Late' ? 'bg-amber-100 text-amber-800' : ''}
                                  ${record.status === 'Excused' ? 'bg-blue-100 text-blue-800' : ''}
                                `}>
                                {record.status}
                              </span>
                            </td>
                          </tr>
                        ))
                      ) : (
                        <tr>
                          <td colSpan="3" className="px-6 py-12 text-center text-slate-400">
                            No history available.
                          </td>
                        </tr>
                      )}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Justification Form Modal */}
      {showJustificationForm && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[100] flex items-center justify-center p-4 animate-fade-in">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-lg overflow-hidden flex flex-col max-h-[90vh]">
            <div className="p-5 border-b border-slate-100 flex justify-between items-center bg-slate-50/50">
              <h2 className="text-xl font-bold text-slate-800 flex items-center gap-2">
                <i className="fas fa-file-medical text-primary-500"></i> Request Leave
              </h2>
              <button
                onClick={handleCloseJustificationForm}
                className="w-8 h-8 rounded-full flex items-center justify-center text-slate-400 hover:bg-slate-200 hover:text-slate-600 transition-colors"
              >
                <i className="fas fa-times"></i>
              </button>
            </div>

            <div className="p-6 overflow-y-auto custom-scrollbar">
              <form onSubmit={handleSubmitJustification} className="space-y-4">
                <div className="grid grid-cols-2 gap-4">
                  <div>
                    <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Index No *</label>
                    <input
                      type="text"
                      className="input-field w-full"
                      value={justificationData.studentIndex}
                      onChange={(e) => setJustificationData({ ...justificationData, studentIndex: e.target.value })}
                      disabled={submitting}
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Phone *</label>
                    <input
                      type="tel"
                      className="input-field w-full"
                      placeholder="07..."
                      value={justificationData.parentPhone}
                      onChange={(e) => setJustificationData({ ...justificationData, parentPhone: e.target.value })}
                      disabled={submitting}
                      required
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Student Name *</label>
                  <input
                    type="text"
                    className="input-field w-full"
                    value={justificationData.studentName}
                    onChange={(e) => setJustificationData({ ...justificationData, studentName: e.target.value })}
                    disabled={submitting}
                    required
                  />
                </div>

                <div>
                  <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Date *</label>
                  <input
                    type="date"
                    className="input-field w-full"
                    value={justificationData.leaveDate}
                    onChange={(e) => setJustificationData({ ...justificationData, leaveDate: e.target.value })}
                    disabled={submitting}
                    required
                  />
                </div>

                <div>
                  <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Reason *</label>
                  <textarea
                    className="input-field w-full min-h-[100px]"
                    placeholder="Please provide details..."
                    value={justificationData.reason}
                    onChange={(e) => setJustificationData({ ...justificationData, reason: e.target.value })}
                    disabled={submitting}
                    required
                  />
                </div>

                <div>
                  <label className="block text-xs font-bold text-slate-500 uppercase tracking-wider mb-1.5 ml-1">Document (Optional)</label>
                  <div className="relative">
                    <input
                      type="file"
                      id="file-upload"
                      className="hidden"
                      accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                      onChange={handleFileChange}
                      disabled={submitting}
                    />
                    <label
                      htmlFor="file-upload"
                      className="input-field w-full flex items-center justify-between cursor-pointer hover:bg-slate-50"
                    >
                      <span className="text-slate-500 truncate text-sm">
                        {justificationData.file ? justificationData.file.name : "Choose file..."}
                      </span>
                      <i className="fas fa-cloud-upload-alt text-primary-500"></i>
                    </label>
                  </div>
                  {justificationData.file && (
                    <button
                      type="button"
                      onClick={() => setJustificationData({ ...justificationData, file: null })}
                      className="text-xs text-rose-500 mt-1 hover:underline pl-1"
                    >
                      Remove file
                    </button>
                  )}
                </div>

                {submitError && (
                  <div className="bg-rose-50 text-rose-600 text-sm p-3 rounded-lg flex items-start gap-2">
                    <i className="fas fa-circle-exclamation mt-0.5"></i>
                    {submitError}
                  </div>
                )}

                <div className="pt-4 flex gap-3">
                  <button
                    type="button"
                    className="flex-1 py-3 bg-slate-100 hover:bg-slate-200 text-slate-600 font-bold rounded-xl transition-colors"
                    onClick={handleCloseJustificationForm}
                    disabled={submitting}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="flex-1 btn-primary py-3 rounded-xl shadow-lg shadow-primary-500/20"
                    disabled={submitting}
                  >
                    {submitting ? (
                      <span className="flex items-center justify-center gap-2">
                        <i className="fas fa-circle-notch fa-spin"></i> Sending...
                      </span>
                    ) : "Submit Request"}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Notifications Modal */}
      {showNotifications && (
        <div className="fixed inset-0 bg-slate-900/60 backdrop-blur-sm z-[100] flex items-center justify-center p-4 animate-fade-in">
          <div className="bg-white rounded-2xl shadow-2xl w-full max-w-md overflow-hidden flex flex-col max-h-[80vh]">
            <div className="p-5 border-b border-slate-100 flex justify-between items-center bg-slate-50/50">
              <h2 className="text-lg font-bold text-slate-800 flex items-center gap-2">
                <i className="fas fa-bell text-amber-500"></i> Notifications
              </h2>
              <div className="flex items-center gap-3">
                {unreadCount > 0 && (
                  <button
                    onClick={handleMarkAllAsRead}
                    className="text-xs font-bold text-primary-600 hover:text-primary-700 hover:underline"
                  >
                    Mark all read
                  </button>
                )}
                <button
                  onClick={() => setShowNotifications(false)}
                  className="w-8 h-8 rounded-full flex items-center justify-center text-slate-400 hover:bg-slate-200 hover:text-slate-600 transition-colors"
                >
                  <i className="fas fa-times"></i>
                </button>
              </div>
            </div>

            <div className="overflow-y-auto custom-scrollbar p-0">
              {notifications.length === 0 ? (
                <div className="py-12 text-center text-slate-400">
                  <div className="w-16 h-16 bg-slate-50 rounded-full flex items-center justify-center mx-auto mb-3">
                    <i className="far fa-bell-slash text-2xl opacity-50"></i>
                  </div>
                  <p>No notifications yet</p>
                </div>
              ) : (
                <div className="divide-y divide-slate-50">
                  {notifications.map((notif) => (
                    <div
                      key={notif._id}
                      onClick={() => !notif.isRead && handleMarkAsRead(notif._id)}
                      className={`
                          p-5 relative transition-colors cursor-pointer
                          ${notif.isRead ? 'bg-white hover:bg-slate-50' : 'bg-amber-50/50 hover:bg-amber-50'}
                        `}
                    >
                      {!notif.isRead && (
                        <div className="absolute right-5 top-5 w-2 h-2 rounded-full bg-rose-500"></div>
                      )}
                      <div className="flex items-start gap-4 pr-4">
                        <div className={`
                              w-10 h-10 rounded-xl flex-shrink-0 flex items-center justify-center text-lg
                              ${notif.type === 'absence' ? 'bg-rose-100 text-rose-500' : 'bg-amber-100 text-amber-600'}
                           `}>
                          <i className={`fas fa-${notif.type === 'absence' ? 'triangle-exclamation' : 'clock'}`}></i>
                        </div>
                        <div>
                          <h4 className={`text-sm font-bold mb-1 ${notif.isRead ? 'text-slate-700' : 'text-slate-900'}`}>
                            {notif.title}
                          </h4>
                          <p className="text-sm text-slate-600 mb-2 leading-relaxed">
                            {notif.message}
                          </p>
                          <div className="flex items-center gap-2 text-xs text-slate-400 font-medium">
                            <span>{dayjs(notif.date).format("MMM DD")}</span>
                            <span>&bull;</span>
                            <span>{dayjs(notif.createdAt).format("h:mm A")}</span>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default ParentView;
