import React, { useState } from "react";

const initial = {
  name: "",
  std_index: "",
  section: "",
  parentName: "",
  parentPhoneNum: "",
  email: ""
};

const initialErrors = {};

const StudentForm = ({ onSubmit, submitting }) => {
  const [form, setForm] = useState(initial);
  const [errors, setErrors] = useState(initialErrors);

  const validateField = (key, value) => {
    switch (key) {
      case "name":
        if (value.trim() === "") return "Student name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Student name must start with a capital letter.";
        return null;
      case "std_index":
        return !/^S\d{4}$/.test(value) ? "Index number must be 'S' followed by 4 digits (e.g., S1234)." : null;
      case "section":
        return !/^\d+[A-Z]$/.test(value) ? "Section must be a number followed by an uppercase letter (e.g., 10A)." : null;
      case "parentName":
        if (value.trim() === "") return "Parent name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Parent name must start with a capital letter.";
        return null;
      case "parentPhoneNum":
        return !/^\+94\s?\d{9}$/.test(value) ? "Phone number must be +94 followed by 9 digits (e.g., +94 712345678)." : null;
      case "email":
        return !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) ? "Please enter a valid email address." : null;
      default:
        return null;
    }
  };

  const handleChange = (key) => (e) => {
    const value = e.target.value;
    const error = validateField(key, value);

    setForm((f) => ({ ...f, [key]: value }));
    setErrors((e) => ({ ...e, [key]: error }));
  };

  const submit = (e) => {
    e.preventDefault();
    // Validate all before submit
    const newErrors = {};
    let hasError = false;
    Object.keys(form).forEach(key => {
      const error = validateField(key, form[key]);
      if (error) {
        newErrors[key] = error;
        hasError = true;
      }
    });

    if (hasError) {
      setErrors(newErrors);
      return;
    }

    onSubmit(form, () => {
      setForm(initial);
      setErrors({});
    });
  };

  const getFieldStatus = (name) => {
    const value = form[name];
    const error = errors[name];
    if (!value) return "neutral"; // Empty input is neutral
    if (error) return "invalid";
    return "valid";
  };

  const getInputClasses = (status) => {
    // Increased padding and explicit height for a larger, more "modern" feel
    const baseClasses = "w-full pl-11 pr-4 py-3.5 bg-slate-50 border border-slate-200 rounded-xl text-slate-900 placeholder-slate-400 focus:outline-none focus:bg-white focus:ring-4 transition-all duration-200 text-base font-medium shadow-sm";

    if (status === "valid") {
      return `${baseClasses} border-emerald-400 focus:border-emerald-500 focus:ring-emerald-500/10 bg-emerald-50/10 text-emerald-900`;
    }
    if (status === "invalid") {
      return `${baseClasses} border-rose-400 focus:border-rose-500 focus:ring-rose-500/10 bg-rose-50/10 text-rose-900`;
    }
    return `${baseClasses} focus:border-primary-500 focus:ring-primary-500/10 hover:bg-white`;
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="card border-0 shadow-2xl shadow-slate-200 overflow-hidden rounded-3xl">
        {/* Modern Gradient Header */}
        <div className="bg-gradient-brand p-10 text-white relative overflow-hidden">
          {/* Decorative circles */}
          <div className="absolute top-0 right-0 w-64 h-64 bg-white/10 rounded-full -mr-20 -mt-20 blur-3xl pointer-events-none"></div>
          <div className="absolute bottom-0 left-0 w-48 h-48 bg-black/5 rounded-full -ml-10 -mb-10 blur-3xl pointer-events-none"></div>

          <div className="relative z-10 flex flex-col items-center text-center">
            <div className="w-16 h-16 rounded-2xl bg-white/20 backdrop-blur-md flex items-center justify-center mb-6 shadow-inner ring-1 ring-white/30">
              <i className="fa-solid fa-user-graduate text-3xl"></i>
            </div>
            <h2 className="text-4xl font-extrabold mb-3 tracking-tight">Add New Student</h2>
            <p className="text-primary-50 text-lg max-w-lg leading-relaxed opacity-90">
              Enter student details below to create a new profile in the system.
            </p>
          </div>
        </div>

        <form onSubmit={submit} className="p-10 bg-white">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-x-10 gap-y-8">

            {/* Student Name */}
            <div className="group md:col-span-1">
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Student Name <span className="text-rose-500">*</span>
              </label>
              <div className="relative">
                <input
                  className={getInputClasses(getFieldStatus("name"))}
                  value={form.name}
                  onChange={handleChange("name")}
                  required
                  placeholder="e.g. John Doe"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none transition-colors group-focus-within:text-primary-500">
                  <i className="fa-solid fa-user"></i>
                </div>
                {getFieldStatus("name") === "valid" && (
                  <div className="absolute right-4 top-1/2 -translate-y-1/2 text-emerald-500 text-xl pointer-events-none animate-fade-in">
                    <i className="fa-solid fa-check-circle"></i>
                  </div>
                )}
              </div>
              {getFieldStatus("name") === "invalid" && (
                <p className="flex items-center mt-2.5 text-sm font-medium text-rose-500 animate-pulse ml-1">
                  <i className="fa-solid fa-circle-exclamation mr-1.5"></i> {errors.name}
                </p>
              )}
            </div>

            {/* Index Number */}
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Index Number <span className="text-rose-500">*</span>
              </label>
              <div className="relative group">
                <input
                  className={getInputClasses(getFieldStatus("std_index"))}
                  value={form.std_index}
                  onChange={handleChange("std_index")}
                  required
                  placeholder="S1234"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none group-focus-within:text-primary-500">
                  <i className="fa-solid fa-id-card"></i>
                </div>
              </div>
              {getFieldStatus("std_index") === "invalid" && <p className="mt-2.5 text-sm font-medium text-rose-500 ml-1"><i className="fa-solid fa-circle-exclamation mr-1"></i> {errors.std_index}</p>}
              {getFieldStatus("std_index") === "neutral" && <p className="mt-2.5 text-sm text-slate-400 ml-1 font-medium">Format: 'S' + 4 digits</p>}
            </div>

            {/* Section */}
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Class / Section <span className="text-rose-500">*</span>
              </label>
              <div className="relative group">
                <input
                  className={getInputClasses(getFieldStatus("section"))}
                  value={form.section}
                  onChange={handleChange("section")}
                  required
                  placeholder="10A"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none group-focus-within:text-primary-500">
                  <i className="fa-solid fa-chalkboard-user"></i>
                </div>
              </div>
              {getFieldStatus("section") === "invalid" && <p className="mt-2.5 text-sm font-medium text-rose-500 ml-1"><i className="fa-solid fa-circle-exclamation mr-1"></i> {errors.section}</p>}
              {getFieldStatus("section") === "neutral" && <p className="mt-2.5 text-sm text-slate-400 ml-1 font-medium">Format: Number + Uppercase Letter</p>}
            </div>

            {/* Email */}
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Student Email <span className="text-rose-500">*</span>
              </label>
              <div className="relative group">
                <input
                  type="email"
                  className={getInputClasses(getFieldStatus("email"))}
                  value={form.email}
                  onChange={handleChange("email")}
                  required
                  placeholder="student@school.com"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none group-focus-within:text-primary-500">
                  <i className="fa-solid fa-envelope"></i>
                </div>
              </div>
              {getFieldStatus("email") === "invalid" && <p className="mt-2.5 text-sm font-medium text-rose-500 ml-1"><i className="fa-solid fa-circle-exclamation mr-1"></i> {errors.email}</p>}
            </div>

            {/* Divider */}
            <div className="col-span-1 md:col-span-2 my-4 relative">
              <div className="absolute inset-0 flex items-center" aria-hidden="true">
                <div className="w-full border-t border-slate-100"></div>
              </div>
              <div className="relative flex justify-center">
                <span className="px-3 bg-white text-sm font-semibold text-slate-400 uppercase tracking-widest">Parent Details</span>
              </div>
            </div>

            {/* Parent Name */}
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Parent Name <span className="text-rose-500">*</span>
              </label>
              <div className="relative group">
                <input
                  className={getInputClasses(getFieldStatus("parentName"))}
                  value={form.parentName}
                  onChange={handleChange("parentName")}
                  required
                  placeholder="Jane Doe"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none group-focus-within:text-primary-500">
                  <i className="fa-solid fa-user-group"></i>
                </div>
              </div>
              {getFieldStatus("parentName") === "invalid" && <p className="mt-2.5 text-sm font-medium text-rose-500 ml-1"><i className="fa-solid fa-circle-exclamation mr-1"></i> {errors.parentName}</p>}
            </div>

            {/* Parent Phone */}
            <div>
              <label className="block text-sm font-bold text-slate-700 mb-2.5 ml-1 uppercase tracking-wider">
                Parent Phone <span className="text-rose-500">*</span>
              </label>
              <div className="relative group">
                <input
                  className={getInputClasses(getFieldStatus("parentPhoneNum"))}
                  value={form.parentPhoneNum}
                  onChange={handleChange("parentPhoneNum")}
                  required
                  placeholder="+94 7XX XXX XXX"
                />
                <div className="absolute left-4 top-1/2 -translate-y-1/2 text-slate-400 text-lg pointer-events-none group-focus-within:text-primary-500">
                  <i className="fa-solid fa-phone"></i>
                </div>
              </div>
              {getFieldStatus("parentPhoneNum") === "invalid" && <p className="mt-2.5 text-sm font-medium text-rose-500 ml-1"><i className="fa-solid fa-circle-exclamation mr-1"></i> {errors.parentPhoneNum}</p>}
              {getFieldStatus("parentPhoneNum") === "neutral" && <p className="mt-2.5 text-sm text-slate-400 ml-1 font-medium">Format: +94 + 9 digits</p>}
            </div>

          </div>

          {/* Submit Action */}
          <div className="mt-12 flex justify-end gap-4">
            <button
              type="button"
              onClick={() => {
                setForm(initial);
                setErrors({});
              }}
              className="px-8 py-4 rounded-2xl font-bold text-slate-500 hover:text-slate-700 hover:bg-slate-50 transition-all active:scale-95"
              disabled={submitting}
            >
              Reset
            </button>
            <button
              type="submit"
              disabled={submitting || Object.values(errors).some(Boolean)}
              className={`
                px-10 py-4 rounded-2xl font-bold uppercase tracking-wide text-lg shadow-xl shadow-primary-500/20 active:scale-95 transition-all duration-300
                ${(submitting || Object.values(errors).some(Boolean))
                  ? "bg-slate-100 text-slate-300 cursor-not-allowed shadow-none border border-slate-200"
                  : "bg-gradient-to-r from-primary-600 to-primary-500 text-white hover:shadow-primary-600/40 hover:-translate-y-1"
                }
              `}
            >
              {submitting ? (
                <span className="flex items-center gap-3">
                  <i className="fa-solid fa-circle-notch fa-spin"></i>
                  Processing...
                </span>
              ) : (
                <span className="flex items-center gap-3">
                  <i className="fa-solid fa-user-plus"></i>
                  Add Student
                </span>
              )}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default StudentForm;
