import React, { useEffect, useState, useRef, useCallback } from 'react';
import { useLocation } from 'react-router-dom';
import axios from 'axios';
import { FileUploaderRegular } from '@uploadcare/react-uploader';
import '@uploadcare/react-uploader/core.css';
import './UpdateNoticeAdmin.css';

const UpdateNoticeAdmin = () => {
  const location = useLocation();
  const [notices, setNotices] = useState([]);
  const [editId, setEditId] = useState(null);
  const [editTitle, setEditTitle] = useState('');
  const [editNotice, setEditNotice] = useState('');
  const [editAttachment, setEditAttachment] = useState(null);
  const [editAttachmentUrl, setEditAttachmentUrl] = useState('');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [deleteId, setDeleteId] = useState(null);
  const uploaderRef = useRef(null);

  const fetchNotices = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.get('/notices');
      setNotices((res.data.notices || []).filter(n => n.createdBy === 'admin'));
    } catch (err) {
      setError('Failed to load notices.');
    }
    setLoading(false);
  };

  const handleEdit = useCallback((notice) => {
    setEditId(notice._id);
    setEditTitle(notice.title);
    setEditNotice(notice.notice);
    // Store existing attachment data
    if (notice.attachment && notice.attachment.url) {
      setEditAttachment(notice.attachment);
      setEditAttachmentUrl(notice.attachment.url);
    } else {
      setEditAttachment(null);
      setEditAttachmentUrl('');
    }
  }, []);

  useEffect(() => {
    fetchNotices();
  }, []);

  // Auto-open edit mode if noticeId is passed via location state
  useEffect(() => {
    if (location.state?.noticeId && notices.length > 0) {
      const notice = notices.find(n => n._id === location.state.noticeId);
      if (notice) {
        handleEdit(notice);
        // Scroll to the notice
        setTimeout(() => {
          const element = document.querySelector('.edit-form');
          if (element) {
            element.scrollIntoView({ behavior: 'smooth', block: 'center' });
          }
        }, 100);
      }
    }
  }, [location.state, notices, handleEdit]);

  const handleDeleteAttachment = () => {
    setEditAttachment(null);
    setEditAttachmentUrl('');
  };

  // Handle Uploadcare file upload success
  const handleUploadSuccess = (file) => {
    if (file && file.cdnUrl) {
      const attachmentData = {
        url: file.cdnUrl,
        uuid: file.uuid,
        contentType: file.mimeType,
        filename: file.name,
        size: file.size
      };
      setEditAttachment(attachmentData);
      setEditAttachmentUrl(file.cdnUrl);
      console.log('File uploaded to Uploadcare:', attachmentData);
    }
  };

  const handleUpdate = async () => {
    try {
      const noticeData = {
        title: editTitle,
        notice: editNotice,
        attachment: editAttachmentUrl === '' ? null : (editAttachment || 'keep')
      };
      await axios.put(`/notices/${editId}`, noticeData, {
        headers: { 'Content-Type': 'application/json' },
      });
      setEditId(null);
      setEditTitle('');
      setEditNotice('');
      setEditAttachment(null);
      setEditAttachmentUrl('');
      fetchNotices();
    } catch (err) {
      console.error('Error updating notice:', err);
      setError('Failed to update notice.');
    }
  };

  const handleDelete = async (id) => {
    setDeleteId(id);
  };

  const confirmDelete = async () => {
    try {
      await axios.delete(`/notices/${deleteId}`);
      setDeleteId(null);
      fetchNotices();
    } catch (err) {
      setError('Failed to delete notice.');
      setDeleteId(null);
    }
  };

  return (
    <>
      <div className="section-title">
        <h2>Admin: Update or Delete Notices</h2>
      </div>
      <div className="update-notice-admin-container">
        {loading ? <p>Loading...</p> : error ? <p className="error-msg">{error}</p> : (
          <div className="notices-list">
            {notices.length === 0 ? <p>No admin notices found.</p> : notices.map(n => (
              <div className="notice-item" key={n._id}>
                {editId === n._id ? (
                  <div className="edit-form">
                    <input
                      type="text"
                      value={editTitle}
                      onChange={e => setEditTitle(e.target.value)}
                      placeholder="Title"
                    />
                    <textarea
                      value={editNotice}
                      onChange={e => setEditNotice(e.target.value)}
                      placeholder="Notice"
                    />
                    {editAttachmentUrl && (
                      <div style={{ marginBottom: '10px', padding: '10px', background: '#f0f0f0', borderRadius: '4px' }}>
                        <p style={{ margin: 0, fontSize: '14px' }}>Current: {editAttachment?.filename || 'Attachment'}</p>
                        <a href={editAttachmentUrl} target="_blank" rel="noopener noreferrer" style={{ fontSize: '12px' }}>View</a>
                        <button type="button" className="btn-primary" onClick={handleDeleteAttachment} style={{ marginLeft: '10px', padding: '4px 8px', fontSize: '12px' }}>Remove</button>
                      </div>
                    )}
                    <label>Upload New Attachment (optional)</label>
                    <FileUploaderRegular
                      ref={uploaderRef}
                      pubkey="e8c9790d2d0cfc27cb41"
                      maxLocalFileSizeBytes={20971520}
                      multiple={false}
                      sourceList="local, url, camera, dropbox"
                      classNameUploader="uc-light"
                      onFileUploadSuccess={handleUploadSuccess}
                    />
                    <button className="btn-primary" onClick={handleUpdate}>Save</button>
                    <button className="btn-primary" onClick={() => setEditId(null)}>Cancel</button>
                  </div>
                ) : (
                  <>
                    <h3>{n.title}</h3>
                    <p>{n.notice}</p>
                    {n.attachment && (
                      <div className="notice-attachment-display" style={{ display: 'flex', alignItems: 'center', gap: '8px', margin: '8px 0' }}>
                        <span role="img" aria-label="attachment" style={{ fontSize: '1.3em' }}>ðŸ“Ž</span>
                        <span style={{ fontWeight: 500 }}>{typeof n.attachment === 'object' ? n.attachment.filename : n.attachment.split('/').pop()}</span>
                        <a href={`/notices/${n._id}/attachment`} target="_blank" rel="noopener noreferrer" className="btn-primary" style={{ padding: '2px 10px', fontSize: '0.95em' }}>Download</a>
                      </div>
                    )}
                    <span className="notice-category">{n.category}</span>
                    <span className="notice-date">{new Date(n.publishedAt).toLocaleString()}</span>
                    <span className="notice-author">By: {n.createdBy}</span>
                    <button className="btn-primary" onClick={() => handleEdit(n)}>Edit</button>
                    <button className="btn-primary" onClick={() => handleDelete(n._id)}>Delete</button>
                  </>
                )}
              </div>
            ))}
          </div>
        )}
        {deleteId && (
          <div className="modal-overlay">
            <div className="modal-card">
              <h3>Confirm Delete</h3>
              <p>Are you sure you want to delete this notice? This action cannot be undone.</p>
              <p style={{ color: '#d32f2f', fontWeight: 500, marginTop: 8 }}>
                If there is an attached file, it will also be deleted.
              </p>
              <div style={{ display: 'flex', gap: '16px', justifyContent: 'center', marginTop: '18px' }}>
                <button className="btn-primary" onClick={confirmDelete}>Delete</button>
                <button className="btn-primary" style={{ background: '#bbb', color: '#222' }} onClick={() => setDeleteId(null)}>Cancel</button>
              </div>
            </div>
            {/* Remove click-to-close behavior on backdrop */}
            <div className="modal-backdrop" style={{ pointerEvents: 'none' }}></div>
          </div>
        )}
      </div>
    </>
  );
};

export default UpdateNoticeAdmin;
