import React, { useState } from "react";

const initial = {
  name: "",
  std_index: "",
  section: "",
  parentName: "",
  parentPhoneNum: ""
};

const StudentForm = ({ onSubmit, submitting }) => {
  const [form, setForm] = useState(initial);
  const [errors, setErrors] = useState({});

  const validateField = (name, value) => {
    let error = "";
    switch (name) {
      case "std_index":
        if (!/^S\d{4}$/.test(value)) {
          error = "Must be 'S' followed by 4 digits (e.g. S1234)";
        }
        break;
      case "section":
        if (!/^\d+[A-Z]$/.test(value)) {
          error = "Must be a number followed by an uppercase letter (e.g. 1A)";
        }
        break;
      case "parentPhoneNum":
        if (!/^\+94\d{9}$/.test(value)) {
          error = "Must start with +94 followed by 9 digits";
        }
        break;
      default:
        break;
    }
    return error;
  };

  const handleChange = (key) => (e) => {
    const value = e.target.value;
    const error = validateField(key, value);

    setForm((f) => ({ ...f, [key]: value }));
    setErrors((e) => ({ ...e, [key]: error }));
  };

  const submit = (e) => {
    e.preventDefault();
    // Validate all before submit
    const newErrors = {};
    let hasError = false;
    Object.keys(form).forEach(key => {
      const error = validateField(key, form[key]);
      if (error) {
        newErrors[key] = error;
        hasError = true;
      }
    });

    if (hasError) {
      setErrors(newErrors);
      return;
    }

    onSubmit(form, () => {
      setForm(initial);
      setErrors({});
    });
  };

  return (
    <form className="student-form" onSubmit={submit}>
      <div className="form-header">
        <h2>Add New Student</h2>
        <p>Please fill in all the required details</p>
      </div>

      <div className="form-grid">
        <div className="form-group">
          <label className="form-label">Student Name *</label>
          <input
            className="form-input"
            value={form.name}
            onChange={handleChange("name")}
            required
            placeholder="John Doe"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Index Number *</label>
          <input
            className={`form-input ${errors.std_index ? "input-error" : ""}`}
            value={form.std_index}
            onChange={handleChange("std_index")}
            required
            placeholder="S1234"
          />
          {errors.std_index && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.std_index}</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Class / Section *</label>
          <input
            className={`form-input ${errors.section ? "input-error" : ""}`}
            value={form.section}
            onChange={handleChange("section")}
            required
            placeholder="10-A"
          />
          {errors.section && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.section}</small>}
        </div>

        <div className="form-group">
          <label className="form-label">Parent Name *</label>
          <input
            className="form-input"
            value={form.parentName}
            onChange={handleChange("parentName")}
            required
            placeholder="Jane Doe"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Parent Phone Number *</label>
          <input
            className={`form-input ${errors.parentPhoneNum ? "input-error" : ""}`}
            value={form.parentPhoneNum}
            onChange={handleChange("parentPhoneNum")}
            required
            placeholder="+94 XXX XXX XXX"
          />
          {errors.parentPhoneNum && <small style={{ color: "red", display: "block", marginTop: "4px" }}>{errors.parentPhoneNum}</small>}
        </div>

        <div className="form-group button-group">
          <button
            type="submit"
            className={`submit-button ${submitting ? "submitting" : ""}`}
            disabled={submitting || Object.values(errors).some(Boolean)}
          >
            {submitting ? (
              <>
                <span className="spinner"></span>
                Adding Student...
              </>
            ) : (
              "Add Student"
            )}
          </button>
        </div>
      </div>
    </form>
  );
};

export default StudentForm;
