import React, { useState } from "react";
import Layout from "../Components/Layout";
import { getAttendanceByStudentIndex } from "../api/client";
import dayjs from "dayjs";

const ParentView = () => {
  const [studentIndex, setStudentIndex] = useState("");
  const [loading, setLoading] = useState(false);
  const [students, setStudents] = useState([]);
  const [todayRecords, setTodayRecords] = useState([]);
  const [allRecords, setAllRecords] = useState([]);
  const [error, setError] = useState("");
  const [searched, setSearched] = useState(false);

  // Justification form states
  const [showJustificationForm, setShowJustificationForm] = useState(false);
  const [justificationData, setJustificationData] = useState({
    studentIndex: "",
    studentName: "",
    reason: "",
    file: null
  });
  const [submitting, setSubmitting] = useState(false);
  const [submitError, setSubmitError] = useState("");

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!studentIndex.trim()) {
      setError("Please enter a student index number");
      return;
    }

    setLoading(true);
    setError("");
    setSearched(false);

    try {
      const { data } = await getAttendanceByStudentIndex(studentIndex);
      setStudents(data.students || []);
      setTodayRecords(data.todayRecords || []);
      setAllRecords(data.allRecords || []);
      setSearched(true);
      setError("");
    } catch (err) {
      console.error("Error fetching attendance:", err);
      setError(err?.response?.data?.message || "Failed to fetch attendance records. Please check the student index number.");
      setStudents([]);
      setTodayRecords([]);
      setAllRecords([]);
      setSearched(false);
    } finally {
      setLoading(false);
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case "Present":
        return { bg: "#d4edda", color: "#155724" };
      case "Absent":
        return { bg: "#f8d7da", color: "#721c24" };
      case "Late":
        return { bg: "#fff3cd", color: "#856404" };
      default:
        return { bg: "#e2e3e5", color: "#6c757d" };
    }
  };

  const handleAddJustification = () => {
    setShowJustificationForm(true);
    setSubmitError("");
  };

  const handleCloseJustificationForm = () => {
    setShowJustificationForm(false);
    setJustificationData({
      studentIndex: "",
      studentName: "",
      reason: "",
      file: null
    });
    setSubmitError("");
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Check file size (max 5MB)
      if (file.size > 5 * 1024 * 1024) {
        setSubmitError("File size should not exceed 5MB");
        return;
      }
      setJustificationData({ ...justificationData, file });
      setSubmitError("");
    }
  };

  const handleSubmitJustification = async (e) => {
    e.preventDefault();

    if (!justificationData.studentIndex.trim()) {
      setSubmitError("Please enter student index number");
      return;
    }
    if (!justificationData.studentName.trim()) {
      setSubmitError("Please enter student name");
      return;
    }
    if (!justificationData.reason.trim()) {
      setSubmitError("Please enter reason for absence/late");
      return;
    }

    setSubmitting(true);
    setSubmitError("");

    try {
      // TODO: Implement API call to submit justification
      console.log("Submitting justification:", justificationData);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1500));

      alert("Leave request submitted successfully! The school will review your request.");
      handleCloseJustificationForm();
    } catch (err) {
      console.error("Error submitting justification:", err);
      setSubmitError("Failed to submit leave request. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  return (
    <Layout>
      <div style={{ textAlign: "center", marginTop: 12 }}>
        <h1 style={{ margin: 0 }}>Parent View</h1>
        <p style={{ color: "var(--subtle)", marginTop: 8 }}>
          Enter your child's student index number to view attendance
        </p>
      </div>

      {/* Search and Add Justification Cards */}
      <div style={{ 
        marginTop: 24, 
        display: "grid", 
        gridTemplateColumns: "1fr 1fr", 
        gap: 16,
        maxWidth: 1200,
        marginLeft: "auto",
        marginRight: "auto"
      }}>
        {/* Search Form Card */}
        <div className="panel">
          <form onSubmit={handleSearch}>
            <div className="field">
              <label>Student Index Number</label>
              <input
                type="text"
                placeholder="Enter student index number (e.g., S001, STD123)"
                value={studentIndex}
                onChange={(e) => setStudentIndex(e.target.value)}
                disabled={loading}
              />
              <small style={{ color: "var(--subtle)", marginTop: 4, display: "block" }}>
                Enter your child's index number as registered with the school
              </small>
            </div>

            {error && (
              <div style={{
                padding: 12,
                backgroundColor: "#f8d7da",
                color: "#721c24",
                borderRadius: 4,
                marginBottom: 16,
                border: "1px solid #f5c6cb"
              }}>
                <i className="fas fa-exclamation-circle" style={{ marginRight: 8 }}></i>
                {error}
              </div>
            )}

            <button 
              type="submit" 
              className="primary" 
              disabled={loading}
              style={{ width: "100%" }}
            >
              {loading ? (
                <>
                  <i className="fas fa-spinner fa-spin" style={{ marginRight: 8 }}></i>
                  Searching...
                </>
              ) : (
                <>
                  <i className="fas fa-search" style={{ marginRight: 8 }}></i>
                  View Attendance
                </>
              )}
            </button>
          </form>
        </div>

        {/* Add Justification Card */}
        <div 
          className="panel" 
          style={{ 
            padding: 20,
            cursor: "pointer",
            transition: "all 0.3s ease",
            border: "2px dashed var(--border)",
            backgroundColor: "#f8f9fa",
            display: "flex",
            flexDirection: "column",
            alignItems: "center",
            justifyContent: "center",
            minHeight: "280px"
          }}
          onClick={handleAddJustification}
          onMouseEnter={(e) => {
            e.currentTarget.style.borderColor = "var(--primary)";
            e.currentTarget.style.backgroundColor = "#e8f4f8";
            e.currentTarget.style.transform = "translateY(-2px)";
          }}
          onMouseLeave={(e) => {
            e.currentTarget.style.borderColor = "var(--border)";
            e.currentTarget.style.backgroundColor = "#f8f9fa";
            e.currentTarget.style.transform = "translateY(0)";
          }}
        >
          <div style={{
            width: 60,
            height: 60,
            borderRadius: "50%",
            backgroundColor: "var(--primary)",
            color: "white",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            fontSize: 28,
            marginBottom: 12
          }}>
            <i className="fas fa-plus"></i>
          </div>
          <h3 style={{ margin: 0, fontSize: 18, color: "var(--primary)", marginBottom: 8 }}>
            Add Justification
          </h3>
          <p style={{ margin: 0, color: "var(--subtle)", fontSize: 14, textAlign: "center" }}>
            Click to add a reason for absence or late arrival
          </p>
        </div>
      </div>

      {/* Results Section */}
      {searched && students.length > 0 && (
        <>
          {/* Student Info Card */}
          <div style={{ marginTop: 24 }}>
            <h2 style={{ marginBottom: 16 }}>Student Information</h2>
            {students.map((student) => (
              <div key={student._id} className="panel" style={{ padding: 20, maxWidth: 600, marginLeft: "auto", marginRight: "auto" }}>
                <div style={{ display: "flex", alignItems: "center", gap: 12, marginBottom: 12 }}>
                  <div style={{
                    width: 50,
                    height: 50,
                    borderRadius: "50%",
                    backgroundColor: "var(--primary)",
                    color: "white",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    fontSize: 20,
                    fontWeight: "bold"
                  }}>
                    {student.name.charAt(0).toUpperCase()}
                  </div>
                  <div>
                    <h3 style={{ margin: 0, fontSize: 18 }}>{student.name}</h3>
                    <p style={{ margin: 0, color: "var(--subtle)", fontSize: 14 }}>
                      Index: {student.std_index}
                    </p>
                  </div>
                </div>
                <div style={{ 
                  display: "grid", 
                  gridTemplateColumns: "1fr 1fr", 
                  gap: 8,
                  marginTop: 12,
                  paddingTop: 12,
                  borderTop: "1px solid var(--border)"
                }}>
                  <div>
                    <small style={{ color: "var(--subtle)" }}>Section</small>
                    <p style={{ margin: 0, fontWeight: 500 }}>{student.section}</p>
                  </div>
                  <div>
                    <small style={{ color: "var(--subtle)" }}>Parent</small>
                    <p style={{ margin: 0, fontWeight: 500 }}>{student.parentName}</p>
                  </div>
                </div>
              </div>
            ))}
          </div>

          {/* Today's Attendance */}
          <div style={{ marginTop: 32 }}>
            <h2 style={{ marginBottom: 16 }}>
              <i className="fas fa-calendar-day" style={{ marginRight: 8, color: "var(--primary)" }}></i>
              Today's Attendance ({dayjs().format("MMMM D, YYYY")})
            </h2>
            {todayRecords.length > 0 ? (
              <div className="panel">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Student Name</th>
                      <th>Index</th>
                      <th>Section</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {todayRecords.map((record) => {
                      const statusStyle = getStatusColor(record.status);
                      return (
                        <tr key={record._id}>
                          <td>{record.student?.name}</td>
                          <td>
                            <span className="badge">{record.student?.std_index}</span>
                          </td>
                          <td>{record.student?.section}</td>
                          <td>
                            <span style={{
                              padding: "4px 12px",
                              borderRadius: 4,
                              backgroundColor: statusStyle.bg,
                              color: statusStyle.color,
                              fontSize: 12,
                              fontWeight: 500
                            }}>
                              {record.status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="panel" style={{ textAlign: "center", padding: 40 }}>
                <i className="fas fa-info-circle" style={{ fontSize: 48, color: "var(--subtle)", marginBottom: 16 }}></i>
                <p style={{ color: "var(--subtle)", margin: 0 }}>
                  No attendance records for today
                </p>
              </div>
            )}
          </div>

          {/* Previous Attendance Records */}
          <div style={{ marginTop: 32 }}>
            <h2 style={{ marginBottom: 16 }}>
              <i className="fas fa-history" style={{ marginRight: 8, color: "var(--primary)" }}></i>
              Previous Attendance Records
            </h2>
            {allRecords.length > 0 ? (
              <div className="panel">
                <table className="table">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Student Name</th>
                      <th>Index</th>
                      <th>Section</th>
                      <th>Status</th>
                    </tr>
                  </thead>
                  <tbody>
                    {allRecords.map((record) => {
                      const statusStyle = getStatusColor(record.status);
                      return (
                        <tr key={record._id}>
                          <td>{dayjs(record.date).format("YYYY-MM-DD")}</td>
                          <td>{record.student?.name}</td>
                          <td>
                            <span className="badge">{record.student?.std_index}</span>
                          </td>
                          <td>{record.student?.section}</td>
                          <td>
                            <span style={{
                              padding: "4px 12px",
                              borderRadius: 4,
                              backgroundColor: statusStyle.bg,
                              color: statusStyle.color,
                              fontSize: 12,
                              fontWeight: 500
                            }}>
                              {record.status}
                            </span>
                          </td>
                        </tr>
                      );
                    })}
                  </tbody>
                </table>
              </div>
            ) : (
              <div className="panel" style={{ textAlign: "center", padding: 40 }}>
                <i className="fas fa-inbox" style={{ fontSize: 48, color: "var(--subtle)", marginBottom: 16 }}></i>
                <p style={{ color: "var(--subtle)", margin: 0 }}>
                  No previous attendance records found
                </p>
              </div>
            )}
          </div>
        </>
      )}
    </Layout>
  );
};

export default ParentView;
