import React, { useState } from "react";
import { User, BookOpen, Phone, Mail, CheckCircle2, AlertCircle, Loader2 } from "lucide-react";

const initial = {
  name: "",
  std_index: "",
  section: "",
  parentName: "",
  parentPhoneNum: "",
  email: ""
};

const initialErrors = {};

const StudentForm = ({ onSubmit, submitting }) => {
  const [form, setForm] = useState(initial);
  const [errors, setErrors] = useState(initialErrors);

  const validateField = (key, value) => {
    switch (key) {
      case "name":
        if (value.trim() === "") return "Student name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Student name must start with a capital letter.";
        return null;
      case "std_index":
        return !/^S\d{4}$/.test(value) ? "Index number must be 'S' followed by 4 digits (e.g., S1234)." : null;
      case "section":
        return !/^\d+[A-Z]$/.test(value) ? "Section must be a number followed by an uppercase letter (e.g., 10A)." : null;
      case "parentName":
        if (value.trim() === "") return "Parent name cannot be empty.";
        if (!/^[A-Z]/.test(value)) return "Parent name must start with a capital letter.";
        return null;
      case "parentPhoneNum":
        return !/^\+94\s?\d{9}$/.test(value) ? "Phone number must be +94 followed by 9 digits (e.g., +94 712345678)." : null;
      case "email":
        return !/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) ? "Please enter a valid email address." : null;
      default:
        return null;
    }
  };

  const handleChange = (key) => (e) => {
    const value = e.target.value;
    const error = validateField(key, value);

    setForm((f) => ({ ...f, [key]: value }));
    setErrors((e) => ({ ...e, [key]: error }));
  };

  const submit = (e) => {
    e.preventDefault();
    const newErrors = {};
    let hasError = false;
    Object.keys(form).forEach(key => {
      const error = validateField(key, form[key]);
      if (error) {
        newErrors[key] = error;
        hasError = true;
      }
    });

    if (hasError) {
      setErrors(newErrors);
      return;
    }

    onSubmit(form, () => {
      setForm(initial);
      setErrors({});
    });
  };

  const getFieldStatus = (name) => {
    const value = form[name];
    const error = errors[name];
    if (value === "") return "neutral";
    if (error) return "invalid";
    return "valid";
  };

  const getInputClasses = (status) => {
    const baseClasses = "w-full px-4 py-3 rounded-lg border-2 transition-all duration-200 focus:outline-none focus:ring-2 focus:ring-offset-1 text-gray-700 placeholder-gray-400";

    if (status === "valid") {
      return `${baseClasses} border-emerald-300 bg-emerald-50 focus:border-emerald-500 focus:ring-emerald-200`;
    }
    if (status === "invalid") {
      return `${baseClasses} border-red-300 bg-red-50 focus:border-red-500 focus:ring-red-200`;
    }
    return `${baseClasses} border-gray-200 bg-white focus:border-teal-500 focus:ring-teal-200`;
  };

  const fieldConfigs = [
    { key: "name", label: "Student Name", placeholder: "John Doe", icon: User },
    { key: "std_index", label: "Index Number", placeholder: "S1234", icon: BookOpen, helpText: "Format: S followed by 4 digits" },
    { key: "section", label: "Class / Section", placeholder: "10A", icon: BookOpen, helpText: "Format: Number + Uppercase Letter (e.g. 1A)" },
    { key: "parentName", label: "Parent Name", placeholder: "Jane Doe", icon: User },
    { key: "parentPhoneNum", label: "Parent Phone Number", placeholder: "+94 712345678", icon: Phone, helpText: "Format: +94 followed by 9 digits" },
    { key: "email", label: "Student Email", placeholder: "student@example.com", icon: Mail, type: "email" },
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 to-slate-100 py-12 px-4 sm:px-6 lg:px-8">
      <div className="max-w-3xl mx-auto">
        <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
          {/* Header Section */}
          <div className="bg-gradient-to-r from-teal-600 to-teal-700 px-8 py-12">
            <h1 className="text-4xl font-bold text-white mb-2">Add New Student</h1>
            <p className="text-teal-100 text-lg">Complete the form to register a new student</p>
          </div>

          {/* Form Section */}
          <form onSubmit={submit} className="p-8 sm:p-12">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              {fieldConfigs.map(({ key, label, placeholder, icon: Icon, helpText, type = "text" }) => {
                const status = getFieldStatus(key);

                return (
                  <div key={key} className={key === "email" ? "md:col-span-2" : ""}>
                    <label className="block text-sm font-semibold text-gray-700 mb-3 flex items-center gap-2">
                      <Icon className="w-4 h-4 text-teal-600" />
                      {label} <span className="text-red-500">*</span>
                    </label>

                    <div className="relative">
                      <input
                        type={type}
                        className={getInputClasses(status)}
                        value={form[key]}
                        onChange={handleChange(key)}
                        placeholder={placeholder}
                        required
                      />

                      {status === "valid" && (
                        <CheckCircle2 className="absolute right-4 top-1/2 -translate-y-1/2 w-5 h-5 text-emerald-500" />
                      )}
                      {status === "invalid" && (
                        <AlertCircle className="absolute right-4 top-1/2 -translate-y-1/2 w-5 h-5 text-red-500" />
                      )}
                    </div>

                    {status === "invalid" && (
                      <div className="flex items-start gap-2 mt-2">
                        <AlertCircle className="w-4 h-4 text-red-500 flex-shrink-0 mt-0.5" />
                        <p className="text-sm text-red-600 font-medium">{errors[key]}</p>
                      </div>
                    )}
                    {status === "valid" && (
                      <div className="flex items-center gap-2 mt-2">
                        <CheckCircle2 className="w-4 h-4 text-emerald-500 flex-shrink-0" />
                        <p className="text-sm text-emerald-600 font-medium">Valid {label}</p>
                      </div>
                    )}
                    {status === "neutral" && helpText && (
                      <p className="text-xs text-gray-500 mt-2">{helpText}</p>
                    )}
                  </div>
                );
              })}
            </div>

            {/* Submit Button */}
            <div className="mt-10">
              <button
                type="submit"
                disabled={submitting || Object.values(errors).some(Boolean)}
                className="w-full bg-gradient-to-r from-teal-600 to-teal-700 hover:from-teal-700 hover:to-teal-800 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed text-white font-semibold py-3 px-6 rounded-lg transition-all duration-200 transform hover:scale-105 disabled:scale-100 shadow-lg hover:shadow-xl flex items-center justify-center gap-2"
              >
                {submitting ? (
                  <>
                    <Loader2 className="w-5 h-5 animate-spin" />
                    <span>Adding Student...</span>
                  </>
                ) : (
                  <>
                    <User className="w-5 h-5" />
                    <span>Add Student</span>
                  </>
                )}
              </button>
            </div>

            {/* Help Text */}
            <p className="text-xs text-gray-500 text-center mt-6">
              All fields marked with <span className="text-red-500">*</span> are required
            </p>
          </form>
        </div>
      </div>
    </div>
  );
};

export default StudentForm;
