import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import { 
  generateAttendanceReport, 
  generateMonthlyReport, 
  getAvailableSections 
} from "../api/client";

const RecordsDownloadPage = () => {
  const [activeTab, setActiveTab] = useState("current");
  const [availableSections, setAvailableSections] = useState([]);
  const [loading, setLoading] = useState(false);
  const [downloading, setDownloading] = useState(false);
  const [sectionsLoading, setSectionsLoading] = useState(true);

  // Current Report States
  const [currentFilters, setCurrentFilters] = useState({
    name: "",
    index: "",
    status: "",
    section: "",
    startDate: "",
    endDate: ""
  });

  // Monthly Report States
  const [monthlyFilters, setMonthlyFilters] = useState({
    name: "",
    index: "",
    section: "",
    month: new Date().getMonth() + 1,
    year: new Date().getFullYear()
  });

  // Search Results
  const [searchResults, setSearchResults] = useState(null);
  const [showResults, setShowResults] = useState(false);

  useEffect(() => {
    loadSections();
  }, []);

  const loadSections = async () => {
    try {
      const response = await getAvailableSections();
      setAvailableSections(response.data || []);
    } catch (error) {
      console.error("Error loading sections:", error);
      setAvailableSections([]);
    }
  };

  const handleCurrentFilterChange = (field, value) => {
    setCurrentFilters(prev => ({ ...prev, [field]: value }));
  };

  const handleMonthlyFilterChange = (field, value) => {
    setMonthlyFilters(prev => ({ ...prev, [field]: value }));
  };

  const searchCurrentRecords = async () => {
    setLoading(true);
    try {
      const params = {};
      if (currentFilters.name) params.name = currentFilters.name;
      if (currentFilters.index) params.index = currentFilters.index;
      if (currentFilters.status) params.status = currentFilters.status;
      if (currentFilters.section) params.section = currentFilters.section;
      if (currentFilters.startDate) params.startDate = currentFilters.startDate;
      if (currentFilters.endDate) params.endDate = currentFilters.endDate;

      const response = await generateAttendanceReport(params);
      setSearchResults({
        type: "current",
        data: response.data,
        filters: currentFilters
      });
      setShowResults(true);
    } catch (error) {
      console.error("Error searching current records:", error);
      alert("Error searching records. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const searchMonthlyRecords = async () => {
    setLoading(true);
    try {
      const params = {};
      if (monthlyFilters.name) params.name = monthlyFilters.name;
      if (monthlyFilters.index) params.index = monthlyFilters.index;
      if (monthlyFilters.section) params.section = monthlyFilters.section;
      params.month = monthlyFilters.month;
      params.year = monthlyFilters.year;

      const response = await generateMonthlyReport(params);
      setSearchResults({
        type: "monthly",
        data: response.data,
        filters: monthlyFilters
      });
      setShowResults(true);
    } catch (error) {
      console.error("Error searching monthly records:", error);
      if (error.response?.status === 404) {
        alert("No records found for the selected month and filters.");
      } else {
        alert("Error searching monthly records. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  const downloadReport = async () => {
    if (!searchResults) return;

    setDownloading(true);
    try {
      let response;
      if (searchResults.type === "current") {
        response = await generateAttendanceReport(searchResults.filters);
      } else {
        response = await generateMonthlyReport(searchResults.filters);
      }

      const blob = new Blob([response.data], { type: 'application/pdf' });
      const url = window.URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.href = url;

      const fileName = searchResults.type === "current" 
        ? `attendance-report-${new Date().toISOString().split('T')[0]}.pdf`
        : `monthly-report-${searchResults.filters.year}-${String(searchResults.filters.month).padStart(2, '0')}.pdf`;

      link.download = fileName;
      document.body.appendChild(link);
      link.click();
      document.body.removeChild(link);
      window.URL.revokeObjectURL(url);
    } catch (error) {
      console.error("Error downloading report:", error);
      alert("Error downloading report. Please try again.");
    } finally {
      setDownloading(false);
    }
  };

  const clearCurrentFilters = () => {
    setCurrentFilters({
      name: "",
      index: "",
      status: "",
      section: "",
      startDate: "",
      endDate: ""
    });
    setShowResults(false);
    setSearchResults(null);
  };

  const clearMonthlyFilters = () => {
    setMonthlyFilters({
      name: "",
      index: "",
      section: "",
      month: new Date().getMonth() + 1,
      year: new Date().getFullYear()
    });
    setShowResults(false);
    setSearchResults(null);
  };

  const months = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const years = Array.from({ length: 5 }, (_, i) => new Date().getFullYear() - i);

  return (
    <Layout>
      <div style={{ textAlign: "center", marginTop: 12 }}>
        <h1 style={{ margin: 0 }}>Records Download Center</h1>
        <p style={{ color: "#666", marginTop: "8px" }}>
          Generate and download comprehensive attendance reports
        </p>
      </div>

      {/* Tab Navigation */}
      <div style={{ 
        display: "flex", 
        justifyContent: "center", 
        marginTop: 20,
        borderBottom: "2px solid #e9ecef"
      }}>
        <button
          onClick={() => setActiveTab("current")}
          style={{
            padding: "12px 24px",
            border: "none",
            background: activeTab === "current" ? "#007bff" : "transparent",
            color: activeTab === "current" ? "white" : "#666",
            cursor: "pointer",
            fontSize: "16px",
            fontWeight: "500",
            borderTopLeftRadius: "8px",
            borderTopRightRadius: "8px",
            transition: "all 0.3s ease"
          }}
        >
          ðŸ“Š Current Attendance Report
        </button>
        <button
          onClick={() => setActiveTab("monthly")}
          style={{
            padding: "12px 24px",
            border: "none",
            background: activeTab === "monthly" ? "#007bff" : "transparent",
            color: activeTab === "monthly" ? "white" : "#666",
            cursor: "pointer",
            fontSize: "16px",
            fontWeight: "500",
            borderTopLeftRadius: "8px",
            borderTopRightRadius: "8px",
            transition: "all 0.3s ease"
          }}
        >
          ðŸ“… Monthly Report
        </button>
      </div>

      {/* Current Attendance Report Section */}
      {activeTab === "current" && (
        <div className="panel" style={{ marginTop: 0, borderTopLeftRadius: 0 }}>
          <div style={{ padding: "24px" }}>
            <h2 style={{ margin: "0 0 20px 0", color: "#333" }}>
              Current Attendance Report
            </h2>
            <p style={{ color: "#666", marginBottom: "24px" }}>
              Search and filter current attendance records to generate detailed reports
            </p>

            {/* Search Filters */}
            <div style={{ 
              display: "grid", 
              gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))", 
              gap: "16px",
              marginBottom: "24px"
            }}>
              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Student Name
                </label>
                <input
                  type="text"
                  value={currentFilters.name}
                  onChange={(e) => handleCurrentFilterChange("name", e.target.value)}
                  placeholder="Enter student name"
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Student Index
                </label>
                <input
                  type="text"
                  value={currentFilters.index}
                  onChange={(e) => handleCurrentFilterChange("index", e.target.value)}
                  placeholder="Enter student index"
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Status
                </label>
                <select
                  value={currentFilters.status}
                  onChange={(e) => handleCurrentFilterChange("status", e.target.value)}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                >
                  <option value="">All Status</option>
                  <option value="Present">Present</option>
                  <option value="Absent">Absent</option>
                  <option value="Late">Late</option>
                </select>
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Section
                </label>
                <select
                  value={currentFilters.section}
                  onChange={(e) => handleCurrentFilterChange("section", e.target.value)}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                >
                  <option value="">All Sections</option>
                  {Array.isArray(availableSections) && availableSections.map(section => (
                    <option key={section} value={section}>{section}</option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Start Date
                </label>
                <input
                  type="date"
                  value={currentFilters.startDate}
                  onChange={(e) => handleCurrentFilterChange("startDate", e.target.value)}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  End Date
                </label>
                <input
                  type="date"
                  value={currentFilters.endDate}
                  onChange={(e) => handleCurrentFilterChange("endDate", e.target.value)}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>
            </div>

            {/* Action Buttons */}
            <div style={{ display: "flex", gap: "12px", justifyContent: "center" }}>
              <button
                onClick={searchCurrentRecords}
                disabled={loading}
                style={{
                  padding: "12px 24px",
                  backgroundColor: "#28a745",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: loading ? "not-allowed" : "pointer",
                  fontSize: "16px",
                  fontWeight: "500",
                  opacity: loading ? 0.7 : 1,
                  transition: "all 0.3s ease"
                }}
              >
                {loading ? "ðŸ” Searching..." : "ðŸ” Search Records"}
              </button>
              <button
                onClick={clearCurrentFilters}
                style={{
                  padding: "12px 24px",
                  backgroundColor: "#6c757d",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  fontSize: "16px",
                  fontWeight: "500",
                  transition: "all 0.3s ease"
                }}
              >
                ðŸ—‘ï¸ Clear Filters
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Monthly Report Section */}
      {activeTab === "monthly" && (
        <div className="panel" style={{ marginTop: 0, borderTopLeftRadius: 0 }}>
          <div style={{ padding: "24px" }}>
            <h2 style={{ margin: "0 0 20px 0", color: "#333" }}>
              Monthly Attendance Report
            </h2>
            <p style={{ color: "#666", marginBottom: "24px" }}>
              Generate comprehensive monthly reports with detailed analytics
            </p>

            {/* Search Filters */}
            <div style={{ 
              display: "grid", 
              gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))", 
              gap: "16px",
              marginBottom: "24px"
            }}>
              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Student Name
                </label>
                <input
                  type="text"
                  value={monthlyFilters.name}
                  onChange={(e) => handleMonthlyFilterChange("name", e.target.value)}
                  placeholder="Enter student name"
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Student Index
                </label>
                <input
                  type="text"
                  value={monthlyFilters.index}
                  onChange={(e) => handleMonthlyFilterChange("index", e.target.value)}
                  placeholder="Enter student index"
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                />
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Section
                </label>
                <select
                  value={monthlyFilters.section}
                  onChange={(e) => handleMonthlyFilterChange("section", e.target.value)}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                >
                  <option value="">All Sections</option>
                  {Array.isArray(availableSections) && availableSections.map(section => (
                    <option key={section} value={section}>{section}</option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Month
                </label>
                <select
                  value={monthlyFilters.month}
                  onChange={(e) => handleMonthlyFilterChange("month", parseInt(e.target.value))}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                >
                  {months.map((month, index) => (
                    <option key={index + 1} value={index + 1}>{month}</option>
                  ))}
                </select>
              </div>

              <div>
                <label style={{ display: "block", marginBottom: "8px", fontWeight: "500" }}>
                  Year
                </label>
                <select
                  value={monthlyFilters.year}
                  onChange={(e) => handleMonthlyFilterChange("year", parseInt(e.target.value))}
                  style={{
                    width: "100%",
                    padding: "10px",
                    border: "1px solid #ddd",
                    borderRadius: "6px",
                    fontSize: "14px"
                  }}
                >
                  {years.map(year => (
                    <option key={year} value={year}>{year}</option>
                  ))}
                </select>
              </div>
            </div>

            {/* Action Buttons */}
            <div style={{ display: "flex", gap: "12px", justifyContent: "center" }}>
              <button
                onClick={searchMonthlyRecords}
                disabled={loading}
                style={{
                  padding: "12px 24px",
                  backgroundColor: "#17a2b8",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: loading ? "not-allowed" : "pointer",
                  fontSize: "16px",
                  fontWeight: "500",
                  opacity: loading ? 0.7 : 1,
                  transition: "all 0.3s ease"
                }}
              >
                {loading ? "ðŸ” Searching..." : "ðŸ” Search Monthly Records"}
              </button>
              <button
                onClick={clearMonthlyFilters}
                style={{
                  padding: "12px 24px",
                  backgroundColor: "#6c757d",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  fontSize: "16px",
                  fontWeight: "500",
                  transition: "all 0.3s ease"
                }}
              >
                ðŸ—‘ï¸ Clear Filters
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Search Results */}
      {showResults && searchResults && (
        <div className="panel" style={{ marginTop: 16 }}>
          <div style={{ padding: "24px" }}>
            <h3 style={{ margin: "0 0 16px 0", color: "#333" }}>
              Search Results
            </h3>

            <div style={{ 
              backgroundColor: "#f8f9fa", 
              padding: "16px", 
              borderRadius: "6px",
              marginBottom: "20px"
            }}>
              <h4 style={{ margin: "0 0 12px 0", color: "#495057" }}>Search Criteria:</h4>
              <div style={{ display: "grid", gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))", gap: "8px" }}>
                {Object.entries(searchResults.filters).map(([key, value]) => {
                  if (value && value !== "") {
                    return (
                      <div key={key} style={{ fontSize: "14px" }}>
                        <strong>{key.charAt(0).toUpperCase() + key.slice(1)}:</strong> {value}
                      </div>
                    );
                  }
                  return null;
                })}
              </div>
            </div>

            <div style={{ 
              backgroundColor: "#e7f3ff", 
              padding: "16px", 
              borderRadius: "6px",
              marginBottom: "20px"
            }}>
              <h4 style={{ margin: "0 0 12px 0", color: "#0056b3" }}>Report Summary:</h4>
              <div style={{ fontSize: "14px", color: "#495057" }}>
                {searchResults.type === "current" ? (
                  <div>
                    <p><strong>Report Type:</strong> Current Attendance Report</p>
                    <p><strong>Total Records Found:</strong> {searchResults.data?.totalRecords || "N/A"}</p>
                    <p><strong>Date Range:</strong> {searchResults.filters.startDate || "All dates"} to {searchResults.filters.endDate || "All dates"}</p>
                  </div>
                ) : (
                  <div>
                    <p><strong>Report Type:</strong> Monthly Report</p>
                    <p><strong>Month/Year:</strong> {months[searchResults.filters.month - 1]} {searchResults.filters.year}</p>
                    <p><strong>Total Records:</strong> {searchResults.data?.totalRecords || "N/A"}</p>
                    <p><strong>Average Attendance:</strong> {searchResults.data?.averageAttendance || "N/A"}%</p>
                  </div>
                )}
              </div>
            </div>

            <div style={{ textAlign: "center" }}>
              <button
                onClick={downloadReport}
                disabled={downloading}
                style={{
                  padding: "16px 32px",
                  backgroundColor: "#dc3545",
                  color: "white",
                  border: "none",
                  borderRadius: "8px",
                  cursor: downloading ? "not-allowed" : "pointer",
                  fontSize: "18px",
                  fontWeight: "600",
                  opacity: downloading ? 0.7 : 1,
                  transition: "all 0.3s ease",
                  boxShadow: "0 4px 12px rgba(220, 53, 69, 0.3)"
                }}
              >
                {downloading ? "ðŸ“¥ Downloading..." : "ðŸ“¥ Download Report"}
              </button>
            </div>
          </div>
        </div>
      )}
    </Layout>
  );
};

export default RecordsDownloadPage;
