import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";
import Layout from "../Components/Layout";

const EditTeacher = () => {
    const navigate = useNavigate();
    const { id } = useParams();
    const [form, setForm] = useState({
        name: "",
        teacherId: "",
        subject: "",
        email: "",
        phone: "",
        profilePic: null,
    });
    const [existingProfilePic, setExistingProfilePic] = useState(null);
    const [loading, setLoading] = useState(false);
    const [errors, setErrors] = useState({});

    // Fetch Teacher Details
    useEffect(() => {
        const fetchTeacher = async () => {
            try {
                // Since there is no "getTeacherById" endpoint exposed directly in the simplified routes provided earlier,
                // we might need to rely on list state or add that endpoint.
                // However, usually GET /teachers/:id is standard.
                // Let's check TeacherRoutes.js again.
                // Ah, it has DELETE /:id, but GET / is all teachers.
                // I should add GET /:id to make this robust, or I can filter from the all list if I had it in context.
                // For now, I'll assume I should Add GET /:id to backend or just filter on frontend if I had a store.
                // Since I modified the backend earlier to add update, I should probably also have added getById.
                // Wait, existing `getTeachers` is for ALL.
                // Let's check if there is a getById. `TeacherRoutes` showed `router.get("/", TeacherController.getTeachers);`
                // I will add `getTeacherById` to backend as well to support this proper edit flow.
                // For this step I will write the frontend code assuming the endpoint exists or will exist soon.
                // Actually, I can just filter from all teachers for now as a fallback if the ID endpoint isn't there,
                // but fetching specific ID is better.
                // Let's implement `getTeacherById` in the Controller in the next step if checking fails?
                // No, let's implement the frontend assuming GET /teachers/:id works or I will implement it.
                // Oh wait, `deleteTeacher` uses `req.params.id`.

                // Let's try to fetch all and find the one. Efficient enough for small app.
                const response = await axios.get("http://localhost:5001/teachers");
                const teacher = response.data.teachers.find(t => t._id === id);

                if (teacher) {
                    setForm({
                        name: teacher.name,
                        teacherId: teacher.teacherId,
                        subject: teacher.subject,
                        email: teacher.email,
                        phone: teacher.phone,
                        profilePic: null // New upload
                    });
                    setExistingProfilePic(teacher.profilePic);
                } else {
                    alert("Teacher not found");
                    navigate("/teachers");
                }
            } catch (error) {
                console.error("Error fetching teacher:", error);
            }
        };
        fetchTeacher();
    }, [id, navigate]);

    const handleChange = (key) => (e) => {
        let value = e.target.type === "file" ? e.target.files[0] : e.target.value;
        setForm({ ...form, [key]: value });
        if (errors[key]) setErrors({ ...errors, [key]: null });
    };

    const validate = () => {
        let tempErrors = {};
        if (!form.name) {
            tempErrors.name = "Name is required";
        } else if (!/^[A-Z]/.test(form.name)) {
            tempErrors.name = "Name must start with a capital letter";
        }

        if (!form.subject) {
            tempErrors.subject = "Class/Section is required";
        } else if (!/^\d+[A-Z]$/.test(form.subject)) {
            tempErrors.subject = "Format: Number + Uppercase Letter (e.g. 10A)";
        }

        if (!form.email) {
            tempErrors.email = "Email is required";
        } else if (!/^[a-zA-Z0-9._%+-]+@gmail\.com$/.test(form.email)) {
            tempErrors.email = "Please enter a valid Gmail address (@gmail.com)";
        }

        if (!form.phone) {
            tempErrors.phone = "Phone is required";
        } else if (!/^\+94\s?\d{9}$/.test(form.phone)) {
            tempErrors.phone = "Format: +94 followed by 9 digits (e.g. +94 712345678)";
        }

        setErrors(tempErrors);
        return Object.keys(tempErrors).length === 0;
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!validate()) return;

        setLoading(true);
        const formData = new FormData();
        Object.keys(form).forEach((key) => {
            if (key === "profilePic" && !form[key]) return; // Don't send null profilePic
            formData.append(key, form[key]);
        });

        try {
            await axios.put(`http://localhost:5001/teachers/${id}`, formData);
            navigate("/teachers");
        } catch (error) {
            console.error("Error updating teacher:", error);
            if (error.response && error.response.data && error.response.data.message) {
                alert(error.response.data.message);
            } else {
                alert("Failed to update teacher");
            }
        } finally {
            setLoading(false);
        }
    };

    const getInputClasses = (error) => {
        return `w-full bg-slate-50 border ${error ? "border-rose-300 focus:border-rose-500 focus:ring-rose-200" : "border-slate-200 focus:border-primary-500 focus:ring-primary-100"
            } rounded-xl px-4 py-3.5 outline-none transition-all focus:ring-4 text-slate-700 placeholder-slate-400`;
    };

    return (
        <Layout>
            <div className="min-h-screen bg-slate-50/50 p-6 md:p-12 flex items-center justify-center">
                <div className="w-full max-w-3xl bg-white rounded-3xl shadow-xl border border-slate-100 overflow-hidden">
                    {/* Header */}
                    <div className="bg-slate-900 p-8 text-center relative overflow-hidden">
                        <div className="absolute top-0 left-0 w-full h-1 bg-gradient-to-r from-primary-500 via-orange-400 to-primary-600"></div>
                        <div className="relative z-10">
                            <h2 className="text-2xl font-bold text-white mb-2">Edit Teacher</h2>
                            <p className="text-slate-400">Update teacher details below</p>
                        </div>
                    </div>

                    <form onSubmit={handleSubmit} className="p-8 md:p-10 space-y-8">
                        {/* Profile Photo Upload */}
                        <div className="flex flex-col items-center justify-center mb-6">
                            <div className="relative group w-24 h-24 rounded-full overflow-hidden border-2 border-slate-200 bg-slate-50 flex items-center justify-center mb-3">
                                {form.profilePic ? (
                                    <img src={URL.createObjectURL(form.profilePic)} alt="Preview" className="w-full h-full object-cover" />
                                ) : existingProfilePic ? (
                                    <img src={`http://localhost:5001/${existingProfilePic.replace(/\\/g, "/")}`} alt="Current" className="w-full h-full object-cover" />
                                ) : (
                                    <i className="fa-solid fa-camera text-slate-300 text-3xl"></i>
                                )}
                                <label className="absolute inset-0 bg-black/40 text-white flex items-center justify-center opacity-0 group-hover:opacity-100 cursor-pointer transition-opacity">
                                    <i className="fa-solid fa-pen text-sm"></i>
                                    <input type="file" className="hidden" accept="image/*" onChange={handleChange("profilePic")} />
                                </label>
                            </div>
                            <span className="text-xs text-slate-500 font-medium">Change Photo (Optional)</span>
                        </div>

                        <div className="grid grid-cols-1 md:grid-cols-2 gap-x-6 gap-y-6">
                            <div className="space-y-1">
                                <label className="text-xs font-bold text-slate-600 uppercase tracking-wider ml-1">Teacher Name</label>
                                <input
                                    type="text"
                                    value={form.name}
                                    onChange={handleChange("name")}
                                    className={getInputClasses(errors.name)}
                                    placeholder="Full Name"
                                />
                                {errors.name && <p className="text-rose-500 text-xs ml-1">{errors.name}</p>}
                            </div>

                            <div className="space-y-1">
                                <label className="text-xs font-bold text-slate-600 uppercase tracking-wider ml-1">Teacher ID</label>
                                <input
                                    type="text"
                                    value={form.teacherId}
                                    readOnly
                                    className={`${getInputClasses(errors.teacherId)} bg-slate-100 cursor-not-allowed`}
                                />
                            </div>

                            <div className="space-y-1">
                                <label className="text-xs font-bold text-slate-600 uppercase tracking-wider ml-1">Class/Section</label>
                                <input
                                    type="text"
                                    value={form.subject}
                                    onChange={handleChange("subject")}
                                    className={getInputClasses(errors.subject)}
                                    placeholder="e.g. 10A"
                                />
                                {errors.subject && <p className="text-rose-500 text-xs ml-1">{errors.subject}</p>}
                            </div>

                            <div className="space-y-1">
                                <label className="text-xs font-bold text-slate-600 uppercase tracking-wider ml-1">Email</label>
                                <input
                                    type="email"
                                    value={form.email}
                                    onChange={handleChange("email")}
                                    className={getInputClasses(errors.email)}
                                    placeholder="Email Address"
                                />
                                {errors.email && <p className="text-rose-500 text-xs ml-1">{errors.email}</p>}
                            </div>

                            <div className="space-y-1 md:col-span-2">
                                <label className="text-xs font-bold text-slate-600 uppercase tracking-wider ml-1">Phone Number</label>
                                <input
                                    type="tel"
                                    value={form.phone}
                                    onChange={handleChange("phone")}
                                    className={getInputClasses(errors.phone)}
                                    placeholder="Content Number"
                                />
                                {errors.phone && <p className="text-rose-500 text-xs ml-1">{errors.phone}</p>}
                            </div>
                        </div>

                        <div className="flex gap-4 pt-6">
                            <button
                                type="button"
                                onClick={() => navigate("/teachers")}
                                className="flex-1 py-3.5 rounded-xl border border-slate-200 text-slate-600 font-bold hover:bg-slate-50 transition-colors"
                            >
                                Cancel
                            </button>
                            <button
                                type="submit"
                                disabled={loading}
                                className={`flex-1 py-3.5 rounded-xl bg-gradient-to-r from-primary-600 to-primary-500 text-white font-bold shadow-lg shadow-primary-500/30 transition-all hover:scale-[1.02] active:scale-[0.98] ${loading ? "opacity-70 cursor-not-allowed" : ""
                                    }`}
                            >
                                {loading ? "Updating..." : "Update Teacher"}
                            </button>
                        </div>
                    </form>
                </div>
            </div>
        </Layout>
    );
};

export default EditTeacher;
