import React, { useState } from "react";

const initial = {
  name: "",
  std_index: "",
  section: "",
  parentName: "",
  parentPhoneNum: ""
};

const StudentForm = ({ onSubmit, submitting }) => {
  const [form, setForm] = useState(initial);

  const set = (key) => (e) => setForm((f) => ({ ...f, [key]: e.target.value }));

  const submit = (e) => {
    e.preventDefault();
    onSubmit(form, () => setForm(initial));
  };

  return (
    <form className="student-form" onSubmit={submit}>
      <div className="form-header">
        <h2>Add New Student</h2>
        <p>Please fill in all the required details</p>
      </div>

      <div className="form-grid">
        <div className="form-group">
          <label className="form-label">Student Name *</label>
          <input
            className="form-input"
            value={form.name}
            onChange={set("name")}
            required
            placeholder="John Doe"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Index Number *</label>
          <input
            className="form-input"
            value={form.std_index}
            onChange={set("std_index")}
            required
            placeholder="S1234"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Class / Section *</label>
          <input
            className="form-input"
            value={form.section}
            onChange={set("section")}
            required
            placeholder="10-A"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Parent Name *</label>
          <input
            className="form-input"
            value={form.parentName}
            onChange={set("parentName")}
            required
            placeholder="Jane Doe"
          />
        </div>

        <div className="form-group">
          <label className="form-label">Parent Phone Number *</label>
          <input
            className="form-input"
            value={form.parentPhoneNum}
            onChange={set("parentPhoneNum")}
            required
            placeholder="+94 XXX XXX XXX"
          />
        </div>

        <div className="form-group button-group">
          <button
            type="submit"
            className={`submit-button ${submitting ? "submitting" : ""}`}
            disabled={submitting}
          >
            {submitting ? (
              <>
                <span className="spinner"></span>
                Adding Student...
              </>
            ) : (
              "Add Student"
            )}
          </button>
        </div>
      </div>
    </form>
  );
};

export default StudentForm;
