import React, { useEffect, useMemo, useState } from "react";
import Layout from "../components/Layout";
import Select from "../components/Select";
import AttendanceRow from "../components/AttendanceRow";
import { getStudents, markAttendance, getAllAttendance } from "../api/client";
import dayjs from "dayjs";

const Attendance = () => {
  const [students, setStudents] = useState([]);
  const [section, setSection] = useState("");
  const [date, setDate] = useState(dayjs().format("YYYY-MM-DD"));
  const [rows, setRows] = useState({});
  const [submitting, setSubmitting] = useState(false);
  const [loading, setLoading] = useState(true);

  const [existingIdsForDate, setExistingIdsForDate] = useState(new Set());
  const [loadingExisting, setLoadingExisting] = useState(false);

  // Leave request states
  const [leaveRequests, setLeaveRequests] = useState([]);
  const [pendingRequestsCount, setPendingRequestsCount] = useState(0);
  const [showLeaveRequests, setShowLeaveRequests] = useState(false);
  const [loadingRequests, setLoadingRequests] = useState(false);

  const load = async () => {
    setLoading(true);
    try {
      const { data } = await getStudents();
      setStudents(data.students || []);
    } finally {
      setLoading(false);
    }
  };

  // Load leave requests for selected section
  const loadLeaveRequests = async (selectedSection) => {
    if (!selectedSection) {
      setLeaveRequests([]);
      setPendingRequestsCount(0);
      return;
    }

    setLoadingRequests(true);
    try {
      // TODO: Replace with actual API calls
      // const [requestsResponse, countResponse] = await Promise.all([
      //   fetch(`http://localhost:5002/leave-requests/section/${selectedSection}?status=pending`),
      //   fetch(`http://localhost:5002/leave-requests/section/${selectedSection}/pending-count`)
      // ]);

      // const requestsData = await requestsResponse.json();
      // const countData = await countResponse.json();

      // Simulated data for now
      const mockRequests = [
        {
          _id: "req1",
          student: {
            _id: "student1",
            name: "John Doe",
            std_index: "001",
            section: selectedSection
          },
          reason: "Medical appointment",
          requestType: "text",
          message: "Doctor appointment for regular checkup",
          startDate: new Date().toISOString(),
          status: "pending",
          createdAt: new Date().toISOString()
        }
      ];

      setLeaveRequests(mockRequests);
      setPendingRequestsCount(mockRequests.length);

    } catch (error) {
      console.error("Error loading leave requests:", error);
      setLeaveRequests([]);
      setPendingRequestsCount(0);
    } finally {
      setLoadingRequests(false);
    }
  };

  // Handle leave request status update
  const handleLeaveRequestUpdate = async (requestId, status, response = "") => {
    try {
      // TODO: Replace with actual API call
      // await fetch(`http://localhost:5002/leave-requests/${requestId}/status`, {
      //   method: 'PUT',
      //   headers: { 'Content-Type': 'application/json' },
      //   body: JSON.stringify({
      //     status,
      //     teacherResponse: response,
      //     reviewedBy: "Teacher Name" // This should come from authentication
      //   })
      // });

      // Update local state
      setLeaveRequests(prev => 
        prev.map(req => 
          req._id === requestId 
            ? { ...req, status, teacherResponse: response }
            : req
        )
      );

      // Update count
      if (status !== "pending") {
        setPendingRequestsCount(prev => Math.max(0, prev - 1));
      }

      alert(`Leave request ${status} successfully`);

    } catch (error) {
      console.error("Error updating leave request:", error);
      alert("Failed to update leave request");
    }
  };

  useEffect(() => { load(); }, []);

  useEffect(() => {
    const id = setInterval(() => {
      setDate(dayjs().format("YYYY-MM-DD"));
    }, 60 * 1000);
    return () => clearInterval(id);
  }, []);

  const sections = useMemo(() => {
    const set = new Set(students.map((s) => s.section));
    return Array.from(set).sort().map((s) => ({ value: s, label: s }));
  }, [students]);

  const visible = useMemo(
    () => (section ? students.filter((s) => s.section === section) : []),
    [students, section]
  );

  useEffect(() => {
    if (!section) {
      setRows({});
      return;
    }
    const map = {};
    visible.forEach((s) => {
      map[s._id] = rows[s._id] ?? { status: "Present" };
    });
    setRows(map);

    // Load leave requests when section changes
    loadLeaveRequests(section);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [visible.length, section]);

  useEffect(() => {
    const loadExisting = async () => {
      if (!section) {
        setExistingIdsForDate(new Set());
        return;
      }
      setLoadingExisting(true);
      try {
        const { data } = await getAllAttendance();
        const list = Array.isArray(data.records) ? data.records : [];
        const ids = new Set(
          list
            .filter((r) => dayjs(r.date).format("YYYY-MM-DD") === date)
            .filter((r) => r.student?.section === section)
            .map((r) => r.student?._id)
            .filter(Boolean)
        );
        setExistingIdsForDate(ids);
      } finally {
        setLoadingExisting(false);
      }
    };
    loadExisting();
  }, [date, section]);

  const updateRow = (studentId, newRow) => {
    setRows((r) => ({ ...r, [studentId]: newRow }));
  };

  const nothingToSubmit = useMemo(() => {
    return visible.every((s) => existingIdsForDate.has(s._id));
  }, [visible, existingIdsForDate]);

  const submit = async () => {
    if (!section) return alert("Select a class/section first");

    setSubmitting(true);
    try {
      const toSubmit = visible.filter((s) => !existingIdsForDate.has(s._id));
      if (toSubmit.length === 0) {
        alert("Attendance already marked for all students in this section for this date.");
        return;
      }

      const payloads = toSubmit.map((s) => {
        const status = rows[s._id]?.status || "Present";
        return {
          studentId: s._id,
          date,
          status,
          notifiedParent: false // Always false when marking attendance
        };
      });

      for (const p of payloads) {
        await markAttendance(p);
      }

      setExistingIdsForDate((prev) => {
        const next = new Set(prev);
        toSubmit.forEach((s) => next.add(s._id));
        return next;
      });

      alert("Attendance marked successfully");
    } catch (e) {
      alert(e?.response?.data?.message || "Failed to mark attendance");
    } finally {
      setSubmitting(false);
    }
  };

  if (!section) {
    return (
      <Layout>
        <div style={{ textAlign: "center", marginTop: 12 }}>
          <h1 style={{ margin: "0 0 20px" }}>Mark Attendance</h1>
          <div style={{ width: "100%", display: "flex", justifyContent: "center" }}>
            <div style={{ display: "flex", gap: 16 }}>
              <div style={{ width: 320 }}>
                <Select
                  label=""
                  value={section}
                  onChange={setSection}
                  options={[{ value: "", label: "Select Section" }, ...sections]}
                />
              </div>
              <div style={{ width: 320 }}>
                <input
                  type="date"
                  value={date}
                  readOnly
                  style={{
                    width: "100%",
                    padding: "10px 12px",
                    background: "var(--muted)",
                    color: "var(--text)",
                    border: "1px solid var(--border)",
                    borderRadius: 8,
                    textAlign: "center",
                    pointerEvents: "none"
                  }}
                  aria-label="Date"
                />
              </div>
            </div>
          </div>
        </div>

        <div className="panel" style={{ padding: 24, textAlign: "center", color: "#aaa", marginTop: 16 }}>
          Select a section to begin marking attendance
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="header" style={{ flexDirection: "column", alignItems: "center" }}>
        <h1 style={{ margin: "0 0 14px" }}>Mark Attendance</h1>
        <div style={{ width: "100%", display: "flex", justifyContent: "center" }}>
          <div style={{ display: "flex", gap: 16, alignItems: "center" }}>
            <div style={{ width: 320 }}>
              <Select
                label=""
                value={section}
                onChange={setSection}
                options={[{ value: "", label: "Select Section" }, ...sections]}
              />
            </div>
            <div style={{ width: 320 }}>
              <input
                type="date"
                value={date}
                readOnly
                style={{
                  width: "100%",
                  padding: "10px 12px",
                  background: "var(--muted)",
                  color: "var(--text)",
                  border: "1px solid var(--border)",
                  borderRadius: 8,
                  textAlign: "center",
                  pointerEvents: "none"
                }}
                aria-label="Date"
              />
            </div>
            {section && (
              <div style={{ position: "relative" }}>
                <button
                  className="btn btn-secondary"
                  onClick={() => setShowLeaveRequests(!showLeaveRequests)}
                  style={{
                    position: "relative",
                    padding: "10px 16px",
                    backgroundColor: pendingRequestsCount > 0 ? "#ff6b6b" : "#6c757d",
                    color: "white",
                    border: "none",
                    borderRadius: 8,
                    cursor: "pointer",
                    fontSize: "14px",
                    fontWeight: "500"
                  }}
                >
                  ðŸ“‹ Leave Requests
                  {pendingRequestsCount > 0 && (
                    <span
                      style={{
                        position: "absolute",
                        top: "-8px",
                        right: "-8px",
                        backgroundColor: "#dc3545",
                        color: "white",
                        borderRadius: "50%",
                        width: "20px",
                        height: "20px",
                        fontSize: "12px",
                        display: "flex",
                        alignItems: "center",
                        justifyContent: "center",
                        fontWeight: "bold"
                      }}
                    >
                      {pendingRequestsCount}
                    </span>
                  )}
                </button>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="panel">
        <table className="table">
          <thead>
            <tr>
              <th>Name</th>
              <th>Index</th>
              <th>Section</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {(loading || loadingExisting) && <tr><td colSpan="4">Loading...</td></tr>}
            {!loading && !loadingExisting && visible.map((s) => (
              <AttendanceRow
                key={s._id}
                student={s}
                row={rows[s._id] || { status: "Present" }}
                onChange={updateRow}
                disabled={existingIdsForDate.has(s._id)}
              />
            ))}
            {!loading && !loadingExisting && visible.length === 0 && (
              <tr><td colSpan="4" style={{ color: "#aaa" }}>No students in this section</td></tr>
            )}
          </tbody>
        </table>

        <div style={{ marginTop: 16, display: "flex", justifyContent: "flex-end", gap: 8, alignItems: "center" }}>
          <button className="ghost" onClick={() => setRows({})}>Reset</button>
          <button
            onClick={submit}
            disabled={submitting || !section || nothingToSubmit}
          >
            {submitting ? "Submitting..." : "Submit Attendance"}
          </button>
        </div>
      </div>

      {/* Leave Requests Panel */}
      {showLeaveRequests && section && (
        <div className="panel" style={{ marginTop: 20 }}>
          <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center", marginBottom: 16 }}>
            <h3 style={{ margin: 0 }}>
              Leave Requests for Section {section}
              {pendingRequestsCount > 0 && (
                <span style={{ 
                  marginLeft: 8, 
                  backgroundColor: "#dc3545", 
                  color: "white", 
                  padding: "2px 8px", 
                  borderRadius: 12, 
                  fontSize: "12px",
                  fontWeight: "normal"
                }}>
                  {pendingRequestsCount} pending
                </span>
              )}
            </h3>
            <button 
              className="btn btn-secondary"
              onClick={() => setShowLeaveRequests(false)}
            >
              Close
            </button>
          </div>

          {loadingRequests ? (
            <div style={{ textAlign: "center", padding: 20, color: "#666" }}>
              Loading leave requests...
            </div>
          ) : leaveRequests.length === 0 ? (
            <div style={{ textAlign: "center", padding: 20, color: "#666", fontStyle: "italic" }}>
              No leave requests found for this section.
            </div>
          ) : (
            <div style={{ display: "grid", gap: 16 }}>
              {leaveRequests.map((request) => (
                <div 
                  key={request._id}
                  style={{
                    border: "1px solid #ddd",
                    borderRadius: 8,
                    padding: 16,
                    backgroundColor: request.status === "pending" ? "#fff8e1" : "#f5f5f5"
                  }}
                >
                  <div style={{ display: "grid", gridTemplateColumns: "1fr auto", gap: 16, alignItems: "start" }}>
                    <div>
                      <div style={{ display: "flex", gap: 16, marginBottom: 8 }}>
                        <strong>{request.student.name}</strong>
                        <span className="badge">{request.student.std_index}</span>
                        <span style={{ 
                          padding: "2px 8px", 
                          borderRadius: 4, 
                          fontSize: "12px",
                          backgroundColor: request.status === "pending" ? "#ffc107" : 
                                         request.status === "approved" ? "#28a745" : "#dc3545",
                          color: "white"
                        }}>
                          {request.status.toUpperCase()}
                        </span>
                      </div>

                      <div style={{ marginBottom: 8 }}>
                        <strong>Reason:</strong> {request.reason}
                      </div>

                      <div style={{ marginBottom: 8 }}>
                        <strong>Date:</strong> {new Date(request.startDate).toLocaleDateString()}
                        {request.endDate && ` - ${new Date(request.endDate).toLocaleDateString()}`}
                      </div>

                      {request.requestType === "text" && request.message && (
                        <div style={{ marginBottom: 8 }}>
                          <strong>Message:</strong> {request.message}
                        </div>
                      )}

                      {request.requestType === "file" && (
                        <div style={{ marginBottom: 8 }}>
                          <strong>Attachment:</strong> 
                          <button 
                            style={{ 
                              marginLeft: 8, 
                              padding: "4px 8px", 
                              fontSize: "12px",
                              backgroundColor: "#007bff",
                              color: "white",
                              border: "none",
                              borderRadius: 4,
                              cursor: "pointer"
                            }}
                            onClick={() => {
                              // TODO: Implement file download
                              alert("File download will be implemented");
                            }}
                          >
                            ðŸ“Ž Download File
                          </button>
                        </div>
                      )}

                      <div style={{ fontSize: "12px", color: "#666" }}>
                        Submitted: {new Date(request.createdAt).toLocaleString()}
                      </div>
                    </div>

                    {request.status === "pending" && (
                      <div style={{ display: "flex", flexDirection: "column", gap: 8 }}>
                        <button
                          className="btn btn-success"
                          style={{ 
                            padding: "6px 12px", 
                            fontSize: "12px",
                            backgroundColor: "#28a745",
                            color: "white",
                            border: "none",
                            borderRadius: 4
                          }}
                          onClick={() => handleLeaveRequestUpdate(request._id, "approved")}
                        >
                          âœ“ Approve
                        </button>
                        <button
                          className="btn btn-danger"
                          style={{ 
                            padding: "6px 12px", 
                            fontSize: "12px",
                            backgroundColor: "#dc3545",
                            color: "white",
                            border: "none",
                            borderRadius: 4
                          }}
                          onClick={() => {
                            const response = prompt("Reason for rejection (optional):");
                            handleLeaveRequestUpdate(request._id, "rejected", response || "");
                          }}
                        >
                          âœ— Reject
                        </button>
                        <button
                          className="btn btn-secondary"
                          style={{ 
                            padding: "6px 12px", 
                            fontSize: "12px",
                            backgroundColor: "#6c757d",
                            color: "white",
                            border: "none",
                            borderRadius: 4
                          }}
                          onClick={() => handleLeaveRequestUpdate(request._id, "reviewed")}
                        >
                          ðŸ‘ Mark as Reviewed
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      )}
    </Layout>
  );
};

export default Attendance;
