import React, { useState, useEffect } from "react";
import Layout from "../components/Layout";
import Select from "../components/Select";

const ParentView = () => {
  const [studentInfo, setStudentInfo] = useState(null);
  const [sections, setSections] = useState([]);
  const [loading, setLoading] = useState(true);

  // Leave request form state
  const [showLeaveForm, setShowLeaveForm] = useState(false);
  const [leaveRequest, setLeaveRequest] = useState({
    studentId: "",
    section: "",
    requestType: "text", // "text" or "file"
    message: "",
    file: null,
    startDate: "",
    endDate: "",
    reason: ""
  });
  const [submitting, setSubmitting] = useState(false);

  // TODO: Add parent authentication validation here
  // For now, we'll simulate parent login
  const parentId = "parent123"; // This should come from authentication

  useEffect(() => {
    loadParentData();
    loadSections();
  }, []);

  const loadParentData = async () => {
    setLoading(true);
    try {
      // TODO: Replace with actual API call to get parent's student info
      // const response = await getStudentsByParent(parentId);

      // Simulated data for now
      const mockStudentData = {
        _id: "student123",
        name: "John Doe",
        std_index: "001",
        section: "12A",
        parentName: "Jane Doe",
        parentPhoneNum: "123-456-7890"
      };

      setStudentInfo(mockStudentData);
      setLeaveRequest(prev => ({
        ...prev,
        studentId: mockStudentData._id,
        section: mockStudentData.section
      }));
    } catch (error) {
      console.error("Error loading parent data:", error);
      alert("Failed to load student information");
    } finally {
      setLoading(false);
    }
  };

  const loadSections = async () => {
    try {
      // TODO: Replace with actual API call
      // const response = await getSections();

      // Simulated sections for now
      const mockSections = [
        { value: "12A", label: "12A" },
        { value: "12B", label: "12B" },
        { value: "11A", label: "11A" },
        { value: "11B", label: "11B" }
      ];

      setSections(mockSections);
    } catch (error) {
      console.error("Error loading sections:", error);
    }
  };

  const handleInputChange = (field) => (e) => {
    const value = field === 'file' ? e.target.files[0] : e.target.value;
    setLeaveRequest(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSubmitLeaveRequest = async (e) => {
    e.preventDefault();

    if (!leaveRequest.reason.trim()) {
      alert("Please provide a reason for the leave request");
      return;
    }

    if (!leaveRequest.startDate) {
      alert("Please select a start date");
      return;
    }

    if (leaveRequest.requestType === "text" && !leaveRequest.message.trim()) {
      alert("Please provide a message for the leave request");
      return;
    }

    if (leaveRequest.requestType === "file" && !leaveRequest.file) {
      alert("Please upload a file for the leave request");
      return;
    }

    setSubmitting(true);
    try {
      // TODO: Replace with actual API call
      // const formData = new FormData();
      // formData.append('studentId', leaveRequest.studentId);
      // formData.append('section', leaveRequest.section);
      // formData.append('requestType', leaveRequest.requestType);
      // formData.append('message', leaveRequest.message);
      // formData.append('startDate', leaveRequest.startDate);
      // formData.append('endDate', leaveRequest.endDate);
      // formData.append('reason', leaveRequest.reason);
      // if (leaveRequest.file) {
      //   formData.append('file', leaveRequest.file);
      // }
      // 
      // await submitLeaveRequest(formData);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      alert("Leave request submitted successfully!");

      // Reset form
      setLeaveRequest({
        studentId: studentInfo._id,
        section: studentInfo.section,
        requestType: "text",
        message: "",
        file: null,
        startDate: "",
        endDate: "",
        reason: ""
      });
      setShowLeaveForm(false);

    } catch (error) {
      console.error("Error submitting leave request:", error);
      alert("Failed to submit leave request. Please try again.");
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <Layout>
        <div className="loading">Loading parent dashboard...</div>
      </Layout>
    );
  }

  if (!studentInfo) {
    return (
      <Layout>
        <div className="error">
          <h2>Access Denied</h2>
          <p>You don't have permission to access this page or no student information found.</p>
        </div>
      </Layout>
    );
  }

  return (
    <Layout>
      <div className="parent-view">
        <div className="header">
          <h1>Parent Dashboard</h1>
          <p>Welcome, {studentInfo.parentName}</p>
        </div>

        {/* Student Information Card */}
        <div className="panel" style={{ marginBottom: 20 }}>
          <h3>Student Information</h3>
          <div className="student-info-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', gap: 16 }}>
            <div>
              <strong>Name:</strong> {studentInfo.name}
            </div>
            <div>
              <strong>Student Index:</strong> <span className="badge">{studentInfo.std_index}</span>
            </div>
            <div>
              <strong>Section:</strong> {studentInfo.section}
            </div>
            <div>
              <strong>Parent Phone:</strong> {studentInfo.parentPhoneNum}
            </div>
          </div>
        </div>

        {/* Leave Request Section */}
        <div className="panel">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 20 }}>
            <h3>Leave Requests</h3>
            <button 
              className="btn btn-primary"
              onClick={() => setShowLeaveForm(!showLeaveForm)}
              disabled={submitting}
            >
              {showLeaveForm ? "Cancel" : "Submit Leave Request"}
            </button>
          </div>

          {showLeaveForm && (
            <form onSubmit={handleSubmitLeaveRequest} className="leave-request-form">
              <div className="form-grid" style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: 16, marginBottom: 16 }}>
                <div className="form-group">
                  <label>Student Section:</label>
                  <Select
                    value={leaveRequest.section}
                    onChange={(value) => setLeaveRequest(prev => ({ ...prev, section: value }))}
                    options={sections}
                    disabled={submitting}
                  />
                </div>

                <div className="form-group">
                  <label>Request Type:</label>
                  <Select
                    value={leaveRequest.requestType}
                    onChange={(value) => setLeaveRequest(prev => ({ ...prev, requestType: value }))}
                    options={[
                      { value: "text", label: "Text Message" },
                      { value: "file", label: "Upload File" }
                    ]}
                    disabled={submitting}
                  />
                </div>

                <div className="form-group">
                  <label>Start Date:</label>
                  <input
                    type="date"
                    value={leaveRequest.startDate}
                    onChange={handleInputChange('startDate')}
                    required
                    disabled={submitting}
                  />
                </div>

                <div className="form-group">
                  <label>End Date (Optional):</label>
                  <input
                    type="date"
                    value={leaveRequest.endDate}
                    onChange={handleInputChange('endDate')}
                    min={leaveRequest.startDate}
                    disabled={submitting}
                  />
                </div>
              </div>

              <div className="form-group" style={{ marginBottom: 16 }}>
                <label>Reason for Leave:</label>
                <input
                  type="text"
                  value={leaveRequest.reason}
                  onChange={handleInputChange('reason')}
                  placeholder="e.g., Medical appointment, Family emergency, etc."
                  required
                  disabled={submitting}
                />
              </div>

              {leaveRequest.requestType === "text" && (
                <div className="form-group" style={{ marginBottom: 16 }}>
                  <label>Message:</label>
                  <textarea
                    value={leaveRequest.message}
                    onChange={handleInputChange('message')}
                    placeholder="Please provide details about the leave request..."
                    rows={4}
                    required
                    disabled={submitting}
                  />
                </div>
              )}

              {leaveRequest.requestType === "file" && (
                <div className="form-group" style={{ marginBottom: 16 }}>
                  <label>Upload File (Medical certificate, etc.):</label>
                  <input
                    type="file"
                    onChange={handleInputChange('file')}
                    accept=".pdf,.jpg,.jpeg,.png,.doc,.docx"
                    required
                    disabled={submitting}
                  />
                  <small style={{ color: '#666', fontSize: '12px' }}>
                    Accepted formats: PDF, JPG, PNG, DOC, DOCX (Max 5MB)
                  </small>
                </div>
              )}

              <div className="form-actions" style={{ display: 'flex', gap: 12 }}>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  disabled={submitting}
                >
                  {submitting ? "Submitting..." : "Submit Request"}
                </button>
                <button 
                  type="button" 
                  className="btn btn-secondary"
                  onClick={() => setShowLeaveForm(false)}
                  disabled={submitting}
                >
                  Cancel
                </button>
              </div>
            </form>
          )}

          {/* Previous Requests (TODO: Implement) */}
          <div style={{ marginTop: 30 }}>
            <h4>Previous Requests</h4>
            <div style={{ padding: 20, textAlign: 'center', color: '#666', fontStyle: 'italic' }}>
              No previous requests found.
            </div>
          </div>
        </div>
      </div>

      <style jsx>{`
        .parent-view {
          max-width: 1200px;
          margin: 0 auto;
        }

        .student-info-grid > div {
          padding: 8px 0;
        }

        .leave-request-form {
          background: #f9f9f9;
          padding: 20px;
          border-radius: 8px;
          border: 1px solid #ddd;
        }

        .form-group {
          display: flex;
          flex-direction: column;
        }

        .form-group label {
          font-weight: 600;
          margin-bottom: 4px;
          color: #333;
        }

        .form-group input,
        .form-group textarea {
          padding: 8px 12px;
          border: 1px solid #ddd;
          border-radius: 4px;
          font-size: 14px;
        }

        .form-group input:focus,
        .form-group textarea:focus {
          outline: none;
          border-color: #007bff;
          box-shadow: 0 0 0 2px rgba(0, 123, 255, 0.25);
        }

        .form-group input:disabled,
        .form-group textarea:disabled {
          background-color: #f5f5f5;
          cursor: not-allowed;
        }

        .loading, .error {
          text-align: center;
          padding: 40px;
        }

        .error h2 {
          color: #dc3545;
          margin-bottom: 10px;
        }
      `}</style>
    </Layout>
  );
};

export default ParentView;
