import React from "react";
import { Link, useLocation } from "react-router-dom";

const Navbar = () => {
  const { pathname } = useLocation();
  const active = (path) => (pathname === path ? 'active' : '');

  return (
    <nav className="compact-navbar">
      <div className="nav-container">
        <div className="nav-content">
          <div className="brand-section">
            <div className="logo-wrapper">
              <div className="logo-icon">ðŸŽ“</div>
            </div>
            <div className="brand-text">
              <h1 className="college-name">WEBSTAR INTERNATIONAL COLLEGE</h1>
              <div className="subtitle">Smart Alert System</div>
            </div>
          </div>

          <div className="nav-links">
            <Link 
              to="/students" 
              className={`nav-btn ${pathname === "/" || pathname === "/students" ? 'active' : ''}`}
            >
              Students
            </Link>
            <Link 
              to="/attendance" 
              className={`nav-btn ${active("/attendance")}`}
            >
              Attendance
            </Link>
            <Link 
              to="/records" 
              className={`nav-btn ${active("/records")}`}
            >
              Records
            </Link>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
